        /* Responsividade */
        @media (max-width: 1200px) {
            .featured-card {
                flex-direction: column;
            }
            
            .featured-image {
                min-height: 200px;
            }
        }

        @media (max-width: 900px) {
            .cards-grid {
                grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            }
            
            .header-actions {
                flex-direction: column-reverse;
                gap: 15px;
                align-items: flex-end;
            }
            
            .search-bar {
                width: 100%;
            }
        }

        @media (max-width: 768px) {
            .dashboard-header {
                flex-direction: column;
                gap: 20px;
                text-align: center;
            }
            
            .header-actions {
                align-items: center;
                width: 100%;
            }
            
            .user-menu {
                flex-direction: column;
                align-items: center;
            }
            
            .user-info {
                text-align: center;
            }
            
            .user-dropdown {
                left: 50%;
                transform: translateX(-50%);
                width: 90vw;
            }
            
            .featured-title {
                font-size: 1.5rem;
            }
        }

        @media (max-width: 480px) {
            .cards-grid {
                grid-template-columns: 1fr;
            }
            
            .card-header {
                height: 120px;
            }
        }

css/ecomania.css.content:
:root {
    --primary-green: #00b894;
    --secondary-green: #00a085;
    --dark-green: #00896b;
    --light-green: #00cec9;
    --accent-purple: #7209b7;
    --background: #0a1f0a;
    --card-background: #1a2e1a;
    --danger: #ef4444;
    --success: #10b981;
    --warning: #f59e0b;
    --text-light: #e8f5e8;
    --text-muted: #a0b4a0;
    --earth-brown: #8b4513;
}

body {
    margin: 0;
    padding: 0;
    background: var(--background);
    font-family: 'Rajdhani', sans-serif;
    color: var(--text-light);
    line-height: 1.6;
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(0, 184, 148, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(114, 9, 183, 0.08) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(0, 160, 133, 0.05) 0%, transparent 50%);
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 5%;
    background: linear-gradient(135deg, rgba(10, 31, 10, 0.95), rgba(0, 184, 148, 0.1));
    position: sticky;
    top: 0;
    z-index: 100;
    backdrop-filter: blur(10px);
    border-bottom: 2px solid var(--primary-green);
    box-shadow: 0 4px 20px rgba(0, 184, 148, 0.3);
}

.logo h1 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    background: linear-gradient(90deg, var(--primary-green), var(--light-green));
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 20px rgba(0, 184, 148, 0.6);
    letter-spacing: 2px;
}

.slogan {
    margin: 5px 0 0;
    font-size: 0.9rem;
    color: var(--accent-purple);
    font-style: italic;
    max-width: 400px;
    line-height: 1.4;
}

nav a {
    color: var(--text-light);
    text-decoration: none;
    margin-left: 25px;
    font-weight: 600;
    transition: all 0.3s ease;
    padding: 8px 16px;
    border-radius: 20px;
    position: relative;
}

nav a:hover {
    color: var(--primary-green);
    background: rgba(0, 184, 148, 0.15);
    transform: translateY(-2px);
}

nav a i {
    margin-right: 8px;
}

.main-container {
    padding: 40px 5%;
    max-width: 1400px;
    margin: 0 auto;
}

.page-title {
    text-align: center;
    margin-bottom: 50px;
    position: relative;
}

.page-title h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    color: var(--primary-green);
    display: inline-block;
    position: relative;
}

.page-title h2::after {
    content: '';
    display: block;
    width: 120px;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-green), var(--accent-purple));
    margin: 15px auto 0;
    border-radius: 2px;
    box-shadow: 0 0 15px rgba(0, 184, 148, 0.6);
}

.search-filter {
    display: flex;
    justify-content: space-between;
    margin-bottom: 40px;
    gap: 20px;
    align-items: center;
}

.search-box {
    flex-grow: 1;
    position: relative;
}

.search-box input {
    width: 100%;
    padding: 15px 25px;
    border-radius: 30px;
    border: 2px solid var(--primary-green);
    background: rgba(26, 46, 26, 0.8);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 184, 148, 0.2);
}

.search-box input:focus {
    outline: none;
    border-color: var(--light-green);
    box-shadow: 0 0 25px rgba(0, 184, 148, 0.4);
    background: rgba(26, 46, 26, 0.9);
}

.search-box input::placeholder {
    color: var(--text-muted);
}

.category-select {
    padding: 15px 25px;
    border-radius: 30px;
    border: 2px solid var(--primary-green);
    background: rgba(26, 46, 26, 0.8);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1.1rem;
    min-width: 220px;
    cursor: pointer;
    transition: all 0.3s ease;
}

.category-select:focus {
    outline: none;
    border-color: var(--light-green);
    box-shadow: 0 0 25px rgba(0, 184, 148, 0.4);
}

.category-select option {
    background: var(--card-background);
    color: var(--text-light);
    padding: 10px;
}

.articles-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
    gap: 30px;
    margin-bottom: 40px;
}

.article-card {
    background: var(--card-background);
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(0, 184, 148, 0.3);
    transition: all 0.3s ease;
    position: relative;
    backdrop-filter: blur(10px);
}

.article-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 15px 35px rgba(0, 184, 148, 0.3);
    border-color: var(--primary-green);
}

.article-image {
    height: 200px;
    background-size: cover;
    background-position: center;
    position: relative;
    overflow: hidden;
}

.article-image::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        45deg,
        rgba(0, 184, 148, 0.2) 0%,
        transparent 50%,
        rgba(114, 9, 183, 0.1) 100%
    );
    z-index: 1;
}

.article-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, transparent 50%, rgba(0, 0, 0, 0.8));
    z-index: 2;
}

.article-category {
    position: absolute;
    top: 15px;
    right: 15px;
    background: linear-gradient(135deg, var(--primary-green), var(--accent-purple));
    color: white;
    padding: 8px 16px;
    border-radius: 25px;
    font-size: 0.85rem;
    font-weight: 600;
    z-index: 3;
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 4px 15px rgba(0, 184, 148, 0.4);
}

.article-content {
    padding: 25px;
}

.article-title {
    font-size: 1.4rem;
    margin: 0 0 12px;
    color: var(--primary-green);
    font-weight: 700;
    line-height: 1.3;
    font-family: 'Orbitron', sans-serif;
}

.article-author {
    font-size: 1rem;
    color: var(--accent-purple);
    margin-bottom: 15px;
    display: block;
    font-weight: 600;
}

.article-excerpt {
    font-size: 1rem;
    margin-bottom: 20px;
    color: var(--text-muted);
    line-height: 1.6;
}

.article-meta {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 20px;
    border-top: 1px solid rgba(0, 184, 148, 0.3);
    padding-top: 15px;
}

.article-date {
    display: flex;
    align-items: center;
}

.article-date i {
    margin-right: 8px;
    color: var(--primary-green);
}

.article-actions {
    display: flex;
    gap: 15px;
}

.action-btn {
    background: none;
    border: none;
    color: var(--text-muted);
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;
    display: flex;
    align-items: center;
    padding: 5px 10px;
    border-radius: 15px;
}

.action-btn i {
    margin-right: 5px;
}

.action-btn:hover {
    color: var(--primary-green);
    background: rgba(0, 184, 148, 0.15);
    transform: scale(1.1);
}

.action-btn.liked {
    color: var(--danger);
}

.action-btn.favorited {
    color: var(--primary-green);
}

.read-more {
    display: inline-block;
    background: linear-gradient(135deg, var(--primary-green), var(--accent-purple));
    color: white;
    padding: 12px 25px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s ease;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 4px 15px rgba(0, 184, 148, 0.4);
}

.read-more:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0, 184, 148, 0.5);
    background: linear-gradient(135deg, var(--light-green), var(--dark-green));
}

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    z-index: 1000;
    justify-content: center;
    align-items: center;
    backdrop-filter: blur(5px);
}

.modal-content {
    background: var(--card-background);
    border-radius: 20px;
    width: 90%;
    max-width: 900px;
    max-height: 90vh;
    overflow-y: auto;
    padding: 40px;
    position: relative;
    border: 2px solid var(--primary-green);
    box-shadow: 0 0 50px rgba(0, 184, 148, 0.4);
}

.close-modal {
    position: absolute;
    top: 20px;
    right: 20px;
    background: none;
    border: none;
    color: var(--primary-green);
    font-size: 2rem;
    cursor: pointer;
    transition: all 0.3s ease;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.close-modal:hover {
    background: rgba(0, 184, 148, 0.15);
    transform: scale(1.1);
}

.modal-title {
    color: var(--primary-green);
    font-size: 2rem;
    margin-bottom: 15px;
    font-family: 'Orbitron', sans-serif;
    line-height: 1.3;
}

.modal-author {
    color: var(--accent-purple);
    font-size: 1.2rem;
    margin-bottom: 10px;
    display: block;
    font-weight: 600;
}

.modal-date {
    color: var(--text-muted);
    margin-bottom: 25px;
    display: block;
    font-size: 1rem;
}

.modal-image {
    width: 100%;
    height: 300px;
    background-size: cover;
    background-position: center;
    margin-bottom: 30px;
    border-radius: 15px;
    border: 2px solid rgba(0, 184, 148, 0.3);
}

.modal-text {
    line-height: 1.8;
    margin-bottom: 40px;
    color: var(--text-light);
    font-size: 1.1rem;
}

.modal-text p {
    margin-bottom: 20px;
}

.comment-section {
    margin-top: 50px;
    border-top: 2px solid rgba(0, 184, 148, 0.3);
    padding-top: 30px;
}

.comment-title {
    color: var(--primary-green);
    font-size: 1.5rem;
    margin-bottom: 25px;
    font-family: 'Orbitron', sans-serif;
}

.comment-form {
    margin-bottom: 35px;
}

.comment-input {
    width: 100%;
    padding: 20px;
    border-radius: 15px;
    border: 2px solid var(--primary-green);
    background: rgba(10, 31, 10, 0.5);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    margin-bottom: 15px;
    min-height: 120px;
    resize: vertical;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.comment-input:focus {
    outline: none;
    border-color: var(--light-green);
    box-shadow: 0 0 25px rgba(0, 184, 148, 0.4);
}

.comment-input::placeholder {
    color: var(--text-muted);
}

.comment-submit {
    background: linear-gradient(135deg, var(--primary-green), var(--accent-purple));
    color: white;
    padding: 12px 30px;
    border-radius: 25px;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-weight: 600;
    transition: all 0.3s ease;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.comment-submit:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 184, 148, 0.5);
}

.comments-list {
    margin-top: 25px;
}

.comment {
    background: rgba(10, 31, 10, 0.3);
    padding: 20px;
    border-radius: 15px;
    margin-bottom: 20px;
    border-left: 4px solid var(--primary-green);
    transition: all 0.3s ease;
}

.comment:hover {
    background: rgba(10, 31, 10, 0.5);
    transform: translateX(5px);
}

.comment-author {
    font-weight: 600;
    color: var(--primary-green);
    margin-bottom: 8px;
    font-size: 1.1rem;
}

.comment-date {
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 12px;
}

.comment-text {
    line-height: 1.6;
    color: var(--text-light);
}

.loading {
    text-align: center;
    padding: 60px;
    color: var(--text-muted);
    font-size: 1.2rem;
}

.loading i {
    color: var(--primary-green);
    margin-right: 10px;
}

.no-results {
    text-align: center;
    padding: 60px;
    color: var(--text-muted);
    font-size: 1.2rem;
}

.no-results i {
    color: var(--primary-green);
    font-size: 3rem;
    margin-bottom: 20px;
    display: block;
}

.no-results h3 {
    color: var(--primary-green);
    margin-bottom: 10px;
}

/* Responsive Design */
@media (max-width: 768px) {
    .logo h1 {
        font-size: 2rem;
    }
    
    .slogan {
        font-size: 0.8rem;
    }
    
    nav a {
        margin-left: 15px;
        font-size: 0.9rem;
    }
    
    .articles-grid {
        grid-template-columns: 1fr;
        gap: 20px;
    }
    
    .search-filter {
        flex-direction: column;
        gap: 15px;
    }
    
    .category-select {
        width: 100%;
    }

    .modal-content {
        width: 95%;
        padding: 25px;
    }

    .page-title h2 {
        font-size: 2rem;
    }
    
    .modal-title {
        font-size: 1.5rem;
    }
}

@media (max-width: 480px) {
    header {
        flex-direction: column;
        gap: 15px;
        text-align: center;
    }
    
    nav {
        display: flex;
        justify-content: center;
        gap: 10px;
    }
    
    nav a {
        margin: 0;
        padding: 6px 12px;
        font-size: 0.85rem;
    }
    
    .main-container {
        padding: 20px 3%;
    }
}

/* Animations */
@keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
}

.pulse {
    animation: pulse 0.5s ease;
}

@keyframes fadeIn {
    from { 
        opacity: 0; 
        transform: translateY(30px); 
    }
    to { 
        opacity: 1; 
        transform: translateY(0); 
    }
}

.fade-in {
    animation: fadeIn 0.6s ease;
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.slide-in-up {
    animation: slideInUp 0.5s ease;
}

/* Notification styles */
.notification {
    position: fixed;
    top: 20px;
    right: 20px;
    background: linear-gradient(135deg, var(--primary-green), var(--accent-purple));
    color: white;
    padding: 15px 25px;
    border-radius: 25px;
    z-index: 10000;
    box-shadow: 0 8px 25px rgba(0, 184, 148, 0.4);
    transform: translateX(400px);
    transition: transform 0.3s ease;
}

.notification.show {
    transform: translateX(0);
}

/* Eco-themed effects */
@keyframes leafFloat {
    0%, 100% { transform: translateY(0) rotate(0deg); }
    25% { transform: translateY(-10px) rotate(5deg); }
    50% { transform: translateY(-5px) rotate(-3deg); }
    75% { transform: translateY(-8px) rotate(2deg); }
}

.article-card::before {
    content: 'üå±';
    position: absolute;
    top: 10px;
    left: 10px;
    font-size: 20px;
    animation: leafFloat 4s ease-in-out infinite;
    z-index: 1;
}

.article-card:nth-child(2n)::before {
    content: 'üçÉ';
    animation-delay: 1s;
}

.article-card:nth-child(3n)::before {
    content: '‚ôªÔ∏è';
    animation-delay: 2s;
}

.article-card:nth-child(4n)::before {
    content: 'üåø';
    animation-delay: 3s;
}

css/gamesmania.css.content:
        :root {
            --roxo: #6a00ff;
            --azul: #00f7ff;
            --verde: #00ffaa;
            --fundo: #0a043c;
            --fundo-card: rgba(22, 33, 62, 0.85);
            --texto: #ffffff;
            --texto-secundario: rgba(255, 255, 255, 0.8);
            --borda: rgba(255, 255, 255, 0.1);
            --erro: #ff4757;
            --sucesso: var(--verde);
            --gaming-red: #ff073a;
            --gaming-gold: #ffd700;
            --gaming-electric: #00ffff;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Rajdhani', sans-serif;
            background: var(--fundo);
            color: var(--texto);
            line-height: 1.6;
            position: relative;
            overflow-x: hidden;
        }

        /* Fundo animado gaming */
        body::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: 
                radial-gradient(circle at 20% 30%, rgba(255, 7, 58, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 80% 70%, rgba(0, 255, 255, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 40% 80%, rgba(255, 215, 0, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 60% 20%, rgba(106, 0, 255, 0.1) 0%, transparent 50%);
            z-index: -1;
            animation: gamingPulse 15s ease-in-out infinite;
        }

        @keyframes gamingPulse {
            0%, 100% { transform: scale(1) rotate(0deg); }
            25% { transform: scale(1.1) rotate(90deg); }
            50% { transform: scale(0.9) rotate(180deg); }
            75% { transform: scale(1.05) rotate(270deg); }
        }

        /* Part√≠culas gaming */
        .gaming-particles {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 0;
        }

        .pixel-particle {
            position: absolute;
            width: 8px;
            height: 8px;
            background: var(--gaming-electric);
            opacity: 0;
            animation: pixelFloat var(--duration) infinite ease-in-out;
        }

        @keyframes pixelFloat {
            0%, 100% { 
                opacity: 0; 
                transform: translateY(100vh) rotate(0deg); 
            }
            10% { 
                opacity: 1; 
                transform: translateY(90vh) rotate(45deg); 
            }
            50% { 
                transform: translateY(50vh) rotate(180deg); 
            }
            90% { 
                opacity: 1; 
                transform: translateY(10vh) rotate(315deg); 
            }
        }

        header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 20px 5%;
            background: linear-gradient(135deg, rgba(10, 4, 60, 0.95), rgba(255, 7, 58, 0.2));
            position: sticky;
            top: 0;
            z-index: 100;
            backdrop-filter: blur(15px);
            border-bottom: 2px solid var(--gaming-red);
            box-shadow: 0 4px 25px rgba(255, 7, 58, 0.3);
        }

        .logo h1 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.5rem;
            margin: 0;
            background: linear-gradient(90deg, var(--gaming-red), var(--gaming-gold), var(--gaming-electric));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            text-shadow: 0 0 20px rgba(255, 7, 58, 0.5);
            letter-spacing: 2px;
            position: relative;
        }

        .logo h1::after {
            content: 'üéÆ';
            position: absolute;
            right: -40px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 1.5rem;
            animation: gameController 3s ease-in-out infinite;
        }

        @keyframes gameController {
            0%, 100% { transform: translateY(-50%) rotate(0deg); }
            50% { transform: translateY(-50%) rotate(15deg) scale(1.1); }
        }

        .slogan {
            margin: 5px 0 0;
            font-size: 0.9rem;
            color: var(--gaming-electric);
            font-style: italic;
            max-width: 400px;
            line-height: 1.4;
        }

        nav a {
            color: var(--texto);
            text-decoration: none;
            margin-left: 25px;
            font-weight: 600;
            transition: all 0.3s ease;
            padding: 8px 16px;
            border-radius: 20px;
            position: relative;
        }

        nav a:hover {
            color: var(--gaming-red);
            background: rgba(255, 7, 58, 0.1);
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(255, 7, 58, 0.2);
        }

        nav a i {
            margin-right: 8px;
        }

        .main-container {
            padding: 40px 5%;
            max-width: 1400px;
            margin: 0 auto;
            position: relative;
            z-index: 1;
        }

        .page-title {
            text-align: center;
            margin-bottom: 50px;
            position: relative;
        }

        .page-title h2 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.8rem;
            margin: 0;
            background: linear-gradient(45deg, var(--gaming-red), var(--gaming-gold), var(--gaming-electric));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            display: inline-block;
            position: relative;
            text-shadow: 0 0 30px rgba(255, 7, 58, 0.5);
        }

        .page-title h2::after {
            content: '';
            display: block;
            width: 150px;
            height: 4px;
            background: linear-gradient(90deg, var(--gaming-red), var(--gaming-gold), var(--gaming-electric));
            margin: 15px auto 0;
            border-radius: 2px;
            box-shadow: 0 0 15px rgba(255, 7, 58, 0.5);
        }

        .search-filter {
            display: flex;
            justify-content: space-between;
            margin-bottom: 40px;
            gap: 20px;
            align-items: center;
        }

        .search-box {
            flex-grow: 1;
            position: relative;
        }

        .search-box input {
            width: 100%;
            padding: 15px 25px;
            border-radius: 30px;
            border: 2px solid var(--gaming-red);
            background: var(--fundo-card);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            font-size: 1.1rem;
            transition: all 0.3s ease;
            box-shadow: 0 4px 15px rgba(255, 7, 58, 0.2);
            backdrop-filter: blur(10px);
        }

        .search-box input:focus {
            outline: none;
            border-color: var(--gaming-electric);
            box-shadow: 0 0 25px rgba(0, 255, 255, 0.4);
            background: rgba(22, 33, 62, 0.95);
        }

        .search-box input::placeholder {
            color: var(--texto-secundario);
        }

        .category-select {
            padding: 15px 25px;
            border-radius: 30px;
            border: 2px solid var(--gaming-red);
            background: var(--fundo-card);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            font-size: 1.1rem;
            min-width: 250px;
            cursor: pointer;
            transition: all 0.3s ease;
            backdrop-filter: blur(10px);
        }

        .category-select:focus {
            outline: none;
            border-color: var(--gaming-electric);
            box-shadow: 0 0 25px rgba(0, 255, 255, 0.4);
        }

        .category-select option {
            background: var(--fundo-card);
            color: var(--texto);
            padding: 10px;
        }

        .articles-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(380px, 1fr));
            gap: 30px;
            margin-bottom: 40px;
        }

        .article-card {
            background: var(--fundo-card);
            border-radius: 20px;
            overflow: hidden;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
            border: 1px solid rgba(255, 7, 58, 0.3);
            transition: all 0.4s ease;
            position: relative;
            backdrop-filter: blur(15px);
        }

        .article-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(135deg, rgba(255, 7, 58, 0.05), rgba(0, 255, 255, 0.05));
            opacity: 0;
            transition: opacity 0.3s ease;
            border-radius: 20px;
        }

        .article-card:hover::before {
            opacity: 1;
        }

        .article-card:hover {
            transform: translateY(-10px) scale(1.02);
            box-shadow: 0 20px 40px rgba(255, 7, 58, 0.3);
            border-color: var(--gaming-red);
        }

        .article-image {
            height: 220px;
            background-size: cover;
            background-position: center;
            position: relative;
            overflow: hidden;
        }

        .article-image::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(to bottom, transparent 40%, rgba(10, 4, 60, 0.9));
            z-index: 2;
        }

        .article-category {
            position: absolute;
            top: 15px;
            right: 15px;
            background: linear-gradient(135deg, var(--gaming-red), var(--gaming-gold));
            color: white;
            padding: 8px 16px;
            border-radius: 25px;
            font-size: 0.85rem;
            font-weight: 600;
            z-index: 3;
            text-transform: uppercase;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(255, 7, 58, 0.4);
        }

        .game-rating {
            position: absolute;
            top: 15px;
            left: 15px;
            background: linear-gradient(135deg, var(--gaming-gold), var(--gaming-electric));
            color: var(--fundo);
            padding: 6px 12px;
            border-radius: 20px;
            font-size: 0.8rem;
            font-weight: 700;
            z-index: 3;
            display: flex;
            align-items: center;
            gap: 5px;
        }

        .article-content {
            padding: 25px;
            position: relative;
        }

        .article-title {
            font-size: 1.5rem;
            margin: 0 0 12px;
            color: var(--gaming-red);
            font-weight: 700;
            line-height: 1.3;
            font-family: 'Orbitron', sans-serif;
        }

        .article-author {
            font-size: 1rem;
            color: var(--gaming-electric);
            margin-bottom: 15px;
            display: block;
            font-weight: 600;
        }

        .article-excerpt {
            font-size: 1rem;
            margin-bottom: 20px;
            color: var(--texto-secundario);
            line-height: 1.6;
        }

        .article-meta {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 0.9rem;
            color: var(--texto-secundario);
            margin-bottom: 20px;
            border-top: 1px solid rgba(255, 7, 58, 0.2);
            padding-top: 15px;
        }

        .article-date {
            display: flex;
            align-items: center;
        }

        .article-date i {
            margin-right: 8px;
            color: var(--gaming-red);
        }

        .article-actions {
            display: flex;
            gap: 15px;
        }

        .action-btn {
            background: none;
            border: none;
            color: var(--texto-secundario);
            cursor: pointer;
            transition: all 0.3s ease;
            font-size: 1rem;
            display: flex;
            align-items: center;
            padding: 5px 10px;
            border-radius: 15px;
        }

        .action-btn i {
            margin-right: 5px;
        }

        .action-btn:hover {
            color: var(--gaming-red);
            background: rgba(255, 7, 58, 0.1);
            transform: scale(1.1);
        }

        .action-btn.liked {
            color: var(--gaming-red);
        }

        .action-btn.favorited {
            color: var(--gaming-gold);
        }

        .read-more {
            display: inline-block;
            background: linear-gradient(135deg, var(--gaming-red), var(--gaming-electric));
            color: white;
            padding: 12px 25px;
            border-radius: 25px;
            text-decoration: none;
            font-weight: 600;
            transition: all 0.3s ease;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            text-transform: uppercase;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(255, 7, 58, 0.3);
        }

        .read-more:hover {
            transform: translateY(-3px);
            box-shadow: 0 8px 25px rgba(255, 7, 58, 0.5);
            background: linear-gradient(135deg, var(--gaming-electric), var(--gaming-gold));
        }

        .loading {
            text-align: center;
            padding: 40px;
            color: var(--gaming-red);
            font-size: 1.2rem;
        }

        .loading i {
            font-size: 2rem;
            margin-bottom: 10px;
            display: block;
        }

        .no-results {
            text-align: center;
            padding: 60px 20px;
            color: var(--texto-secundario);
        }

        .no-results i {
            font-size: 4rem;
            color: var(--gaming-red);
            margin-bottom: 20px;
            display: block;
        }

        .no-results h3 {
            color: var(--gaming-red);
            margin-bottom: 10px;
            font-family: 'Orbitron', sans-serif;
        }

        /* Modal */
        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.95);
            z-index: 1000;
            justify-content: center;
            align-items: center;
            backdrop-filter: blur(10px);
        }

        .modal-content {
            background: var(--fundo-card);
            border-radius: 25px;
            width: 90%;
            max-width: 900px;
            max-height: 90vh;
            overflow-y: auto;
            padding: 40px;
            position: relative;
            border: 2px solid var(--gaming-red);
            box-shadow: 0 0 50px rgba(255, 7, 58, 0.5);
            backdrop-filter: blur(20px);
        }

        .close-modal {
            position: absolute;
            top: 20px;
            right: 20px;
            background: none;
            border: none;
            color: var(--gaming-red);
            font-size: 2rem;
            cursor: pointer;
            transition: all 0.3s ease;
            width: 40px;
            height: 40px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .close-modal:hover {
            background: rgba(255, 7, 58, 0.2);
            transform: scale(1.1);
        }

        .modal-title {
            color: var(--gaming-red);
            font-size: 2.2rem;
            margin-bottom: 15px;
            font-family: 'Orbitron', sans-serif;
            line-height: 1.3;
        }

        .modal-author {
            color: var(--gaming-electric);
            font-size: 1.2rem;
            margin-bottom: 10px;
            display: block;
            font-weight: 600;
        }

        .modal-date {
            color: var(--texto-secundario);
            margin-bottom: 25px;
            display: block;
            font-size: 1rem;
        }

        .modal-image {
            width: 100%;
            height: 350px;
            background-size: cover;
            background-position: center;
            margin-bottom: 30px;
            border-radius: 15px;
            border: 2px solid rgba(255, 7, 58, 0.3);
        }

        .modal-text {
            line-height: 1.8;
            margin-bottom: 40px;
            color: var(--texto);
            font-size: 1.1rem;
        }

        .modal-text p {
            margin-bottom: 20px;
        }

        .comment-section {
            margin-top: 50px;
            border-top: 2px solid rgba(255, 7, 58, 0.3);
            padding-top: 30px;
        }

        .comment-title {
            color: var(--gaming-red);
            font-size: 1.6rem;
            margin-bottom: 25px;
            font-family: 'Orbitron', sans-serif;
        }

        .comment-form {
            margin-bottom: 35px;
        }

        .comment-input {
            width: 100%;
            padding: 20px;
            border-radius: 15px;
            border: 2px solid var(--gaming-red);
            background: rgba(10, 4, 60, 0.7);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            margin-bottom: 15px;
            min-height: 120px;
            resize: vertical;
            font-size: 1rem;
            transition: all 0.3s ease;
        }

        .comment-input:focus {
            outline: none;
            border-color: var(--gaming-electric);
            box-shadow: 0 0 25px rgba(0, 255, 255, 0.4);
        }

        .comment-input::placeholder {
            color: var(--texto-secundario);
        }

        .comment-submit {
            background: linear-gradient(135deg, var(--gaming-red), var(--gaming-electric));
            color: white;
            padding: 12px 30px;
            border-radius: 25px;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            font-weight: 600;
            transition: all 0.3s ease;
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        .comment-submit:hover {
            transform: translateY(-2px);
            box-shadow: 0 8px 25px rgba(255, 7, 58, 0.4);
        }

        /* Anima√ß√µes */
        .fade-in {
            opacity: 0;
            animation: fadeIn 0.6s ease forwards;
        }

        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(30px); }
            to { opacity: 1; transform: translateY(0); }
        }

        /* Responsividade */
        @media (max-width: 768px) {
            .search-filter {
                flex-direction: column;
                gap: 15px;
            }

            .category-select {
                min-width: 100%;
            }

            .articles-grid {
                grid-template-columns: 1fr;
                gap: 20px;
            }

            .article-card {
                margin: 0 10px;
            }

            .page-title h2 {
                font-size: 2.2rem;
            }

            .logo h1 {
                font-size: 2rem;
            }

            .logo h1::after {
                right: -30px;
                font-size: 1.2rem;
            }

            nav a {
                margin-left: 15px;
                font-size: 0.9rem;
            }

            .modal-content {
                padding: 25px;
                width: 95%;
            }

            .modal-title {
                font-size: 1.8rem;
            }
        }

        @media (max-width: 480px) {
            .main-container {
                padding: 20px 3%;
            }

            .article-content {
                padding: 20px;
            }

            .article-title {
                font-size: 1.3rem;
            }

            .logo h1::after {
                display: none;
            }
        }

css/historymania.css.content:
   :root {
            --roxo: #6a00ff;
            --azul: #00f7ff;
            --verde: #00ffaa;
            --destaque: #6a4c93;
            --fundo: #0a043c;
            --fundo-card: rgba(22, 33, 62, 0.85);
            --texto: #ffffff;
            --texto-secundario: rgba(255, 255, 255, 0.8);
            --borda: rgba(255, 255, 255, 0.1);
            --erro: #ff4757;
            --sucesso: var(--verde);
            --history-gold: #d4af37;
            --history-bronze: #cd7f32;
            --history-ancient: #8b4513;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Rajdhani', sans-serif;
            background: var(--fundo);
            color: var(--texto);
            line-height: 1.6;
            position: relative;
            overflow-x: hidden;
        }

        /* Fundo hist√≥rico animado */
        body::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: 
                radial-gradient(circle at 30% 20%, rgba(106, 76, 147, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 70% 80%, rgba(212, 175, 55, 0.12) 0%, transparent 50%),
                radial-gradient(circle at 20% 70%, rgba(205, 127, 50, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 80% 30%, rgba(139, 69, 19, 0.08) 0%, transparent 50%);
            z-index: -1;
            animation: historicalDrift 30s ease-in-out infinite;
        }

        @keyframes historicalDrift {
            0%, 100% { transform: scale(1) rotate(0deg); opacity: 0.8; }
            25% { transform: scale(1.1) rotate(90deg); opacity: 1; }
            50% { transform: scale(0.9) rotate(180deg); opacity: 0.9; }
            75% { transform: scale(1.05) rotate(270deg); opacity: 0.7; }
        }

        /* Part√≠culas hist√≥ricas */
        .history-particles {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 0;
        }

        .scroll-particle {
            position: absolute;
            width: 16px;
            height: 16px;
            background: var(--destaque);
            border-radius: 2px;
            opacity: 0;
            animation: ancientScroll var(--duration) infinite ease-in-out;
        }

        @keyframes ancientScroll {
            0%, 100% { 
                opacity: 0; 
                transform: translateY(100vh) rotate(0deg) scale(0.5); 
            }
            20% { 
                opacity: 0.8; 
                transform: translateY(80vh) rotate(45deg) scale(1); 
            }
            50% { 
                opacity: 1;
                transform: translateY(50vh) rotate(180deg) scale(1.1); 
            }
            80% { 
                opacity: 0.6; 
                transform: translateY(20vh) rotate(315deg) scale(1); 
            }
        }

        header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 20px 5%;
            background: linear-gradient(135deg, rgba(10, 4, 60, 0.95), rgba(106, 76, 147, 0.2));
            position: sticky;
            top: 0;
            z-index: 100;
            backdrop-filter: blur(15px);
            border-bottom: 2px solid var(--destaque);
            box-shadow: 0 4px 25px rgba(106, 76, 147, 0.3);
        }

        .logo h1 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.5rem;
            margin: 0;
            background: linear-gradient(90deg, var(--destaque), var(--history-gold), var(--history-bronze));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            text-shadow: 0 0 20px rgba(106, 76, 147, 0.5);
            letter-spacing: 2px;
            position: relative;
        }

        .logo h1::after {
            content: 'üìú';
            position: absolute;
            right: -40px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 1.5rem;
            animation: ancientScroll 5s ease-in-out infinite;
        }

        @keyframes ancientScroll {
            0%, 100% { transform: translateY(-50%) rotate(0deg); }
            25% { transform: translateY(-50%) rotate(-10deg) scale(1.1); }
            50% { transform: translateY(-50%) rotate(0deg) scale(1.2); }
            75% { transform: translateY(-50%) rotate(10deg) scale(1.1); }
        }

        .slogan {
            margin: 5px 0 0;
            font-size: 0.9rem;
            color: var(--destaque);
            font-style: italic;
            max-width: 400px;
            line-height: 1.4;
        }

        nav a {
            color: var(--texto);
            text-decoration: none;
            margin-left: 25px;
            font-weight: 600;
            transition: all 0.3s ease;
            padding: 8px 16px;
            border-radius: 20px;
            position: relative;
        }

        nav a:hover {
            color: var(--destaque);
            background: rgba(106, 76, 147, 0.1);
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(106, 76, 147, 0.2);
        }

        nav a i {
            margin-right: 8px;
        }

