            color: transparent;
            text-shadow: 0 0 20px rgba(106, 0, 255, 0.5);
            letter-spacing: 2px;
            position: relative;
        }

        .logo h1::after {
            content: 'üîÆ';
            position: absolute;
            right: -40px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 1.5rem;
            animation: glow 2s ease-in-out infinite alternate;
        }

        @keyframes glow {
