// Fun√ß√£o para aguardar autentica√ß√£o com timeout melhorado
async function waitForAuth(timeout = 10000) {
    return new Promise((resolve) => {
        let resolved = false;

        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (!resolved) {
                resolved = true;
                unsubscribe();
                resolve(user);
            }
        });

        setTimeout(() => {
            if (!resolved) {
                resolved = true;
                unsubscribe();
                resolve(null);
            }
        }, timeout);
