// Renderiza lista de coment√°rios
function renderComments(comments) {
    if (comments.length === 0) {
        return '<p style="color: var(--texto-secundario); text-align: center; padding: 1rem;">Ainda n√£o h√° coment√°rios. Seja o primeiro!</p>';
