// Inicializa√ß√£o das part√≠culas qu√≠micas
document.addEventListener('DOMContentLoaded', () => {
    createChemistryParticles();
    setupSectionNavigation();
    console.log('üß™ Laborat√≥rio de Qu√≠mica inicializado com navega√ß√£o funcional');
});

// Criar part√≠culas qu√≠micas animadas
function createChemistryParticles() {
    const particlesContainer = document.getElementById('chemistryParticles');
    const particleCount = 25;
    
    for (let i = 0; i < particleCount; i++) {
        const particle = document.createElement('div');
        particle.className = 'molecule-particle';
        
        // Propriedades aleat√≥rias para cada part√≠cula
        const size = Math.random() * 10 + 6;
        const x = Math.random() * 100;
        const duration = Math.random() * 12 + 18;
        const delay = Math.random() * 5;
        
        // Cores aleat√≥rias baseadas no tema da qu√≠mica
        const colors = ['#f72585', '#e91e63', '#ff006e', '#c77dff'];
        const color = colors[Math.floor(Math.random() * colors.length)];
        
        particle.style.cssText = `
            left: ${x}%;
            width: ${size}px;
            height: ${size}px;
            background: ${color};
            --duration: ${duration}s;
            animation-delay: ${delay}s;
        `;
        
        particlesContainer.appendChild(particle);
    }
}

// Configurar navega√ß√£o das se√ß√µes
function setupSectionNavigation() {
    // Adicionar event listeners para todos os bot√µes de se√ß√£o
    const sectionButtons = document.querySelectorAll('.section-button');
    
    sectionButtons.forEach(button => {
        button.addEventListener('click', (e) => {
            // Adicionar efeito visual de clique
            const ripple = document.createElement('span');
            const rect = button.getBoundingClientRect();
            const size = Math.max(rect.width, rect.height);
            const x = e.clientX - rect.left - size / 2;
            const y = e.clientY - rect.top - size / 2;
            
            ripple.style.cssText = `
                position: absolute;
                width: ${size}px;
                height: ${size}px;
                left: ${x}px;
                top: ${y}px;
                background: rgba(255, 255, 255, 0.3);
                border-radius: 50%;
                transform: scale(0);
                animation: ripple 0.6s ease-out;
                pointer-events: none;
            `;
            
            button.style.position = 'relative';
            button.style.overflow = 'hidden';
            button.appendChild(ripple);
            
            setTimeout(() => {
                if (button.contains(ripple)) {
                    button.removeChild(ripple);
                }
            }, 600);
        });
    });
    
    // Adicionar efeitos de hover nos cards
    const sectionCards = document.querySelectorAll('.section-card');
    
    sectionCards.forEach(card => {
        card.addEventListener('mouseenter', () => {
            // Efeito de part√≠culas no hover
            createHoverParticles(card);
        });
        
        card.addEventListener('click', (e) => {
            const button = card.querySelector('.section-button');
            if (button && !e.target.closest('.section-button')) {
                // Se clicou no card mas n√£o no bot√£o, simular clique no bot√£o
                button.click();
            }
        });
    });
}

// Criar part√≠culas no hover dos cards
function createHoverParticles(card) {
    const particleCount = 5;
    
    for (let i = 0; i < particleCount; i++) {
        const particle = document.createElement('div');
        particle.style.cssText = `
            position: absolute;
            width: 6px;
            height: 6px;
            background: #f72585;
            border-radius: 50%;
            pointer-events: none;
            left: ${Math.random() * 100}%;
            top: ${Math.random() * 100}%;
            animation: hoverParticle 1s ease-out forwards;
            z-index: 10;
        `;
        
        card.appendChild(particle);
        
        setTimeout(() => {
            if (card.contains(particle)) {
                card.removeChild(particle);
            }
        }, 1000);
    }
}

// Fun√ß√µes de navega√ß√£o para compatibilidade com vers√µes anteriores
function navigateToSection(sectionId) {
    const sectionUrls = {
        'reactions': '/lab/reaquim.html',
        'periodic': '/lab/periotab.html',
        'synthesis': '/lab/sin.html',
        'analysis': '/lab/anls.html'
    };
    
    const url = sectionUrls[sectionId];
    if (url) {
        // Efeito de transi√ß√£o antes de navegar
        document.body.style.opacity = '0.8';
        setTimeout(() => {
            window.location.href = url;
        }, 300);
    }
}

// Fun√ß√£o para mostrar notifica√ß√µes
function showNotification(message, type = 'info') {
    const notification = document.createElement('div');
    notification.style.cssText = `
        position: fixed;
        top: 20px;
        right: 20px;
        padding: 15px 20px;
        border-radius: 10px;
        color: white;
        font-weight: 600;
        z-index: 10000;
        animation: slideIn 0.3s ease-out;
        max-width: 300px;
    `;
    
    const colors = {
        'info': '#4fc3f7',
        'success': '#00e676',
        'warning': '#ffc107',
        'error': '#ff3d00'
    };
    
    notification.style.background = colors[type] || colors.info;
    notification.textContent = message;
    
    document.body.appendChild(notification);
    
    setTimeout(() => {
        notification.style.animation = 'slideOut 0.3s ease-out forwards';
        setTimeout(() => {
            if (document.body.contains(notification)) {
                document.body.removeChild(notification);
            }
        }, 300);
    }, 3000);
}

// Adicionar estilos para anima√ß√µes
const style = document.createElement('style');
style.textContent = `
    @keyframes ripple {
        to {
            transform: scale(4);
            opacity: 0;
        }
    }
    
    @keyframes hoverParticle {
        0% {
            transform: scale(0) translateY(0);
            opacity: 1;
        }
        100% {
            transform: scale(1) translateY(-20px);
            opacity: 0;
        }
    }
    
    @keyframes slideIn {
        from {
            transform: translateX(100%);
            opacity: 0;
        }
        to {
            transform: translateX(0);
            opacity: 1;
        }
    }
    
    @keyframes slideOut {
        from {
            transform: translateX(0);
            opacity: 1;
        }
        to {
            transform: translateX(100%);
            opacity: 0;
        }
    }
`;
document.head.appendChild(style);

// Fun√ß√£o para pr√©-carregar p√°ginas (otimiza√ß√£o)
function preloadPages() {
    const pages = [
        'reacoes-quimicas.html',
        'tabela-periodica-3d.html',
        'sintese-compostos.html',
        'analise-molecular.html'
    ];
    
    pages.forEach(page => {
        const link = document.createElement('link');
        link.rel = 'prefetch';
        link.href = page;
        document.head.appendChild(link);
    });
}

// Executar pr√©-carregamento ap√≥s um delay
setTimeout(preloadPages, 2000);

// Log de inicializa√ß√£o
console.log(`
üß™ LABMANIA - Laborat√≥rio de Qu√≠mica
‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
‚úÖ Sistema de navega√ß√£o ativo
‚úÖ Part√≠culas qu√≠micas animadas
‚úÖ Efeitos visuais carregados
‚úÖ Pr√©-carregamento de p√°ginas
‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
üöÄ Laborat√≥rio pronto para uso!
