// Firebase configuration and initialization
import { initializeApp } from 'https://www.gstatic.com/firebasejs/10.7.1/firebase-app.js';
import { 
    getAuth, 
    signInWithEmailAndPassword, 
    createUserWithEmailAndPassword,
    signInWithPopup,
    GoogleAuthProvider,
    onAuthStateChanged,
    signOut,
    updateProfile,
    sendPasswordResetEmail
} from 'https://www.gstatic.com/firebasejs/10.7.1/firebase-auth.js';
import { 
    getFirestore,
    collection,
    addDoc,
    getDocs,
    doc,
    getDoc,
    updateDoc,
    deleteDoc,
    query,
    where,
    orderBy,
    limit,
    onSnapshot,
    serverTimestamp,
    increment
} from 'https://www.gstatic.com/firebasejs/10.7.1/firebase-firestore.js';
import { 
    getStorage,
    ref,
    uploadBytes,
    getDownloadURL,
    deleteObject
} from 'https://www.gstatic.com/firebasejs/10.7.1/firebase-storage.js';

// Firebase configuration
const firebaseConfig = {
    apiKey: "AIzaSyD8Mx4XldG5eXsVat2FyqqlEt4EnUvrQ80",
    authDomain: "fatomania-de805.firebaseapp.com",
    projectId: "fatomania-de805",
    storageBucket: "fatomania-de805.appspot.com",
    messagingSenderId: "274835802694",
    appId: "1:274835802694:web:f97de5be2cdcf5043c9847",
    measurementId: "G-B610GPX286"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const storage = getStorage(app);
const googleProvider = new GoogleAuthProvider();

// Configure Google provider
googleProvider.addScope('email');
googleProvider.addScope('profile');

// Export Firebase functions
window.firebaseAuth = {
    auth,
    googleProvider,
    signInWithEmailAndPassword,
    createUserWithEmailAndPassword,
    signInWithPopup,
    onAuthStateChanged,
    signOut,
    updateProfile,
    sendPasswordResetEmail
};

window.firebaseDB = {
    db,
    collection,
    addDoc,
    getDocs,
    doc,
    getDoc,
    updateDoc,
    deleteDoc,
    query,
    where,
    orderBy,
    limit,
    onSnapshot,
    serverTimestamp,
    increment
};

window.firebaseStorage = {
    storage,
    ref,
    uploadBytes,
    getDownloadURL,
    deleteObject
};

// Função para verificar auth com timeout
async function waitForAuth(timeout = 5000) {
    return new Promise((resolve) => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            unsubscribe();
            resolve(user);
        });

        setTimeout(() => {
            unsubscribe();
            resolve(null);
        }, timeout);
    });
}

// Middleware para páginas protegidas
async function requireAuth(redirectTo = '/logsin.html') {
    const user = await waitForAuth();
    if (!user) {
        localStorage.setItem('redirectAfterLogin', window.location.pathname);
        window.location.href = redirectTo;
        return false;
    }
    return user;
}

// Export functions globally
window.requireAuth = requireAuth;
window.waitForAuth = waitForAuth;

// Auth state listener with improved redirect logic
let authStateProcessed = false;
onAuthStateChanged(auth, (user) => {
    // Evita múltiplos processamentos do mesmo estado
    if (authStateProcessed) return;

    const currentPath = window.location.pathname;
    console.log('Auth state changed. User:', user?.email, 'Path:', currentPath);

    if (user) {
        console.log('User is signed in:', user.email);

        // Páginas de login onde devemos redirecionar para dashboard
        const loginPages = ['/', '/index.html', '/logsin.html'];
        const isOnLoginPage = loginPages.includes(currentPath) || 
                             currentPath.endsWith('index.html') || 
                             currentPath.endsWith('logsin.html');

        if (isOnLoginPage) {
            // Verifica se há um redirecionamento pendente
            const redirectPath = localStorage.getItem('redirectAfterLogin');
            if (redirectPath && redirectPath !== currentPath) {
                localStorage.removeItem('redirectAfterLogin');
                console.log('Redirecting to saved path:', redirectPath);
                window.location.replace(redirectPath);
            } else {
                console.log('Redirecting to dashboard...');
                window.location.replace('/dashboard.html');
            }
            authStateProcessed = true;
        }
    } else {
        console.log('User is signed out');

        // Páginas protegidas que requerem autenticação
        const protectedPatterns = ['/dashboard', '/submit-', '/admin-panel'];
        const isProtectedPage = protectedPatterns.some(pattern => currentPath.includes(pattern));

        if (isProtectedPage) {
            console.log('Protected page accessed without auth, saving path and redirecting...');
            localStorage.setItem('redirectAfterLogin', currentPath);
            window.location.replace('/logsin.html');
            authStateProcessed = true;
        }
    }
});

css/artmania.css.content:
       :root {
            --roxo: #6a00ff;
            --azul: #00f7ff;
            --verde: #00ffaa;
            --destaque: #00b4d8;
            --fundo: #0a043c;
            --fundo-card: rgba(22, 33, 62, 0.85);
            --texto: #ffffff;
            --texto-secundario: rgba(255, 255, 255, 0.8);
            --borda: rgba(255, 255, 255, 0.1);
            --erro: #ff4757;
            --sucesso: var(--verde);
            --art-gold: #ffd166;
            --art-coral: #f77f00;
            --art-teal: #06ffa5;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Rajdhani', sans-serif;
            background: var(--fundo);
            color: var(--texto);
            line-height: 1.6;
            position: relative;
            overflow-x: hidden;
        }

        /* Fundo artístico animado */
        body::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: 
                radial-gradient(circle at 25% 25%, rgba(0, 180, 216, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 75% 75%, rgba(255, 209, 102, 0.12) 0%, transparent 50%),
                radial-gradient(circle at 50% 10%, rgba(247, 127, 0, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 20% 80%, rgba(6, 255, 165, 0.08) 0%, transparent 50%);
            z-index: -1;
            animation: artisticFlow 25s ease-in-out infinite;
        }

        @keyframes artisticFlow {
            0%, 100% { transform: scale(1) rotate(0deg); opacity: 0.8; }
            25% { transform: scale(1.1) rotate(90deg); opacity: 1; }
            50% { transform: scale(0.9) rotate(180deg); opacity: 0.9; }
            75% { transform: scale(1.05) rotate(270deg); opacity: 0.7; }
        }

        /* Partículas artísticas */
        .art-particles {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 0;
        }

        .brush-particle {
            position: absolute;
            width: 12px;
            height: 12px;
            background: var(--destaque);
            border-radius: 50%;
            opacity: 0;
            animation: brushStroke var(--duration) infinite ease-in-out;
        }

        @keyframes brushStroke {
            0%, 100% { 
                opacity: 0; 
                transform: translateY(100vh) rotate(0deg) scale(0.5); 
            }
            20% { 
                opacity: 0.8; 
                transform: translateY(80vh) rotate(72deg) scale(1); 
            }
            50% { 
                opacity: 1;
                transform: translateY(50vh) rotate(180deg) scale(1.2); 
            }
            80% { 
                opacity: 0.6; 
                transform: translateY(20vh) rotate(288deg) scale(1); 
            }
        }

        header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 20px 5%;
            background: linear-gradient(135deg, rgba(10, 4, 60, 0.95), rgba(0, 180, 216, 0.2));
            position: sticky;
            top: 0;
            z-index: 100;
            backdrop-filter: blur(15px);
            border-bottom: 2px solid var(--destaque);
            box-shadow: 0 4px 25px rgba(0, 180, 216, 0.3);
        }

        .logo h1 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.5rem;
            margin: 0;
            background: linear-gradient(90deg, var(--destaque), var(--art-gold), var(--art-coral));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            text-shadow: 0 0 20px rgba(0, 180, 216, 0.5);
            letter-spacing: 2px;
            position: relative;
        }

        .logo h1::after {
            content: '🎨';
            position: absolute;
            right: -40px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 1.5rem;
            animation: paintBrush 4s ease-in-out infinite;
        }

        @keyframes paintBrush {
            0%, 100% { transform: translateY(-50%) rotate(0deg); }
            25% { transform: translateY(-50%) rotate(-15deg) scale(1.1); }
            50% { transform: translateY(-50%) rotate(0deg) scale(1.2); }
            75% { transform: translateY(-50%) rotate(15deg) scale(1.1); }
        }

        .slogan {
            margin: 5px 0 0;
            font-size: 0.9rem;
            color: var(--destaque);
            font-style: italic;
            max-width: 400px;
            line-height: 1.4;
        }

        nav a {
            color: var(--texto);
            text-decoration: none;
            margin-left: 25px;
            font-weight: 600;
            transition: all 0.3s ease;
            padding: 8px 16px;
            border-radius: 20px;
            position: relative;
        }

        nav a:hover {
            color: var(--destaque);
            background: rgba(0, 180, 216, 0.1);
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 180, 216, 0.2);
        }

        nav a i {
            margin-right: 8px;
        }

        .main-container {
            padding: 40px 5%;
            max-width: 1400px;
            margin: 0 auto;
            position: relative;
            z-index: 1;
        }

        .page-title {
            text-align: center;
            margin-bottom: 50px;
            position: relative;
        }

        .page-title h2 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.8rem;
            margin: 0;
            background: linear-gradient(45deg, var(--destaque), var(--art-gold), var(--art-coral), var(--art-teal));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            display: inline-block;
            position: relative;
            text-shadow: 0 0 30px rgba(0, 180, 216, 0.5);
        }

        .page-title h2::after {
            content: '';
            display: block;
            width: 150px;
            height: 4px;
            background: linear-gradient(90deg, var(--destaque), var(--art-gold), var(--art-coral));
            margin: 15px auto 0;
            border-radius: 2px;
            box-shadow: 0 0 15px rgba(0, 180, 216, 0.5);
        }

        .search-filter {
            display: flex;
            justify-content: space-between;
            margin-bottom: 40px;
            gap: 20px;
            align-items: center;
        }

        .search-box {
            flex-grow: 1;
            position: relative;
        }

        .search-box input {
            width: 100%;
            padding: 15px 25px;
            border-radius: 30px;
            border: 2px solid var(--destaque);
            background: var(--fundo-card);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            font-size: 1.1rem;
            transition: all 0.3s ease;
            box-shadow: 0 4px 15px rgba(0, 180, 216, 0.2);
            backdrop-filter: blur(10px);
        }

        .search-box input:focus {
            outline: none;
            border-color: var(--art-gold);
            box-shadow: 0 0 25px rgba(255, 209, 102, 0.4);
            background: rgba(22, 33, 62, 0.95);
        }

        .search-box input::placeholder {
            color: var(--texto-secundario);
        }

        .category-select {
            padding: 15px 25px;
            border-radius: 30px;
            border: 2px solid var(--destaque);
            background: var(--fundo-card);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            font-size: 1.1rem;
            min-width: 250px;
            cursor: pointer;
            transition: all 0.3s ease;
            backdrop-filter: blur(10px);
        }

        .category-select:focus {
            outline: none;
            border-color: var(--art-gold);
            box-shadow: 0 0 25px rgba(255, 209, 102, 0.4);
        }

        .category-select option {
            background: var(--fundo-card);
            color: var(--texto);
            padding: 10px;
        }

        .articles-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(380px, 1fr));
            gap: 30px;
            margin-bottom: 40px;
        }

        .article-card {
            background: var(--fundo-card);
            border-radius: 20px;
            overflow: hidden;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
            border: 1px solid rgba(0, 180, 216, 0.3);
            transition: all 0.4s ease;
            position: relative;
            backdrop-filter: blur(15px);
        }

        .article-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(135deg, rgba(0, 180, 216, 0.05), rgba(255, 209, 102, 0.05));
            opacity: 0;
            transition: opacity 0.3s ease;
            border-radius: 20px;
        }

        .article-card:hover::before {
            opacity: 1;
        }

        .article-card:hover {
            transform: translateY(-10px) scale(1.02);
            box-shadow: 0 20px 40px rgba(0, 180, 216, 0.3);
            border-color: var(--destaque);
        }

        .article-image {
            height: 220px;
            background-size: cover;
            background-position: center;
            position: relative;
            overflow: hidden;
        }

        .article-image::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(to bottom, transparent 40%, rgba(10, 4, 60, 0.9));
            z-index: 2;
        }

        .article-category {
            position: absolute;
            top: 15px;
            right: 15px;
            background: linear-gradient(135deg, var(--destaque), var(--art-gold));
            color: white;
            padding: 8px 16px;
            border-radius: 25px;
            font-size: 0.85rem;
            font-weight: 600;
            z-index: 3;
            text-transform: uppercase;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(0, 180, 216, 0.4);
        }

        .art-style {
            position: absolute;
            top: 15px;
            left: 15px;
            background: linear-gradient(135deg, var(--art-coral), var(--art-teal));
            color: white;
            padding: 6px 12px;
            border-radius: 20px;
            font-size: 0.8rem;
            font-weight: 600;
            z-index: 3;
            display: flex;
            align-items: center;
            gap: 5px;
        }

        .article-content {
            padding: 25px;
            position: relative;
        }

        .article-title {
            font-size: 1.5rem;
            margin: 0 0 12px;
            color: var(--destaque);
            font-weight: 700;
            line-height: 1.3;
            font-family: 'Orbitron', sans-serif;
        }

        .article-author {
            font-size: 1rem;
            color: var(--art-gold);
            margin-bottom: 15px;
            display: block;
            font-weight: 600;
        }

        .article-excerpt {
            font-size: 1rem;
            margin-bottom: 20px;
            color: var(--texto-secundario);
            line-height: 1.6;
        }

        .article-meta {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 0.9rem;
            color: var(--texto-secundario);
            margin-bottom: 20px;
            border-top: 1px solid rgba(0, 180, 216, 0.2);
            padding-top: 15px;
        }

        .article-date {
            display: flex;
            align-items: center;
        }

        .article-date i {
            margin-right: 8px;
            color: var(--destaque);
        }

        .article-actions {
            display: flex;
            gap: 15px;
        }

        .action-btn {
            background: none;
            border: none;
            color: var(--texto-secundario);
            cursor: pointer;
            transition: all 0.3s ease;
            font-size: 1rem;
            display: flex;
            align-items: center;
            padding: 5px 10px;
            border-radius: 15px;
        }

        .action-btn i {
            margin-right: 5px;
        }

        .action-btn:hover {
            color: var(--destaque);
            background: rgba(0, 180, 216, 0.1);
            transform: scale(1.1);
        }

        .action-btn.liked {
            color: var(--art-coral);
        }

        .action-btn.favorited {
            color: var(--art-gold);
        }

        .read-more {
            display: inline-block;
            background: linear-gradient(135deg, var(--destaque), var(--art-gold));
            color: white;
            padding: 12px 25px;
            border-radius: 25px;
            text-decoration: none;
            font-weight: 600;
            transition: all 0.3s ease;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            text-transform: uppercase;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(0, 180, 216, 0.3);
        }

        .read-more:hover {
            transform: translateY(-3px);
            box-shadow: 0 8px 25px rgba(0, 180, 216, 0.5);
            background: linear-gradient(135deg, var(--art-gold), var(--art-coral));
        }

        .loading {
            text-align: center;
            padding: 40px;
            color: var(--destaque);
            font-size: 1.2rem;
        }

        .loading i {
            font-size: 2rem;
            margin-bottom: 10px;
            display: block;
        }

        .no-results {
            text-align: center;
            padding: 60px 20px;
            color: var(--texto-secundario);
        }

        .no-results i {
            font-size: 4rem;
            color: var(--destaque);
            margin-bottom: 20px;
            display: block;
        }

        .no-results h3 {
            color: var(--destaque);
            margin-bottom: 10px;
            font-family: 'Orbitron', sans-serif;
        }

        /* Modal */
        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.95);
            z-index: 1000;
            justify-content: center;
            align-items: center;
            backdrop-filter: blur(10px);
        }

        .modal-content {
            background: var(--fundo-card);
            border-radius: 25px;
            width: 90%;
            max-width: 900px;
            max-height: 90vh;
            overflow-y: auto;
            padding: 40px;
            position: relative;
            border: 2px solid var(--destaque);
            box-shadow: 0 0 50px rgba(0, 180, 216, 0.5);
            backdrop-filter: blur(20px);
        }

        .close-modal {
            position: absolute;
            top: 20px;
            right: 20px;
            background: none;
            border: none;
            color: var(--destaque);
            font-size: 2rem;
            cursor: pointer;
            transition: all 0.3s ease;
            width: 40px;
            height: 40px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .close-modal:hover {
            background: rgba(0, 180, 216, 0.2);
            transform: scale(1.1);
        }

        .modal-title {
            color: var(--destaque);
            font-size: 2.2rem;
            margin-bottom: 15px;
            font-family: 'Orbitron', sans-serif;
            line-height: 1.3;
        }

        .modal-author {
            color: var(--art-gold);
            font-size: 1.2rem;
            margin-bottom: 10px;
            display: block;
            font-weight: 600;
        }

        .modal-date {
            color: var(--texto-secundario);
            margin-bottom: 25px;
            display: block;
            font-size: 1rem;
        }

        .modal-image {
            width: 100%;
            height: 350px;
            background-size: cover;
            background-position: center;
            margin-bottom: 30px;
            border-radius: 15px;
            border: 2px solid rgba(0, 180, 216, 0.3);
        }

        .modal-text {
            line-height: 1.8;
            margin-bottom: 40px;
            color: var(--texto);
            font-size: 1.1rem;
        }

        .modal-text p {
            margin-bottom: 20px;
        }

        .comment-section {
            margin-top: 50px;
            border-top: 2px solid rgba(0, 180, 216, 0.3);
            padding-top: 30px;
        }

        .comment-title {
            color: var(--destaque);
            font-size: 1.6rem;
            margin-bottom: 25px;
            font-family: 'Orbitron', sans-serif;
        }

        .comment-form {
            margin-bottom: 35px;
        }

        .comment-input {
            width: 100%;
            padding: 20px;
            border-radius: 15px;
            border: 2px solid var(--destaque);
            background: rgba(10, 4, 60, 0.7);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            margin-bottom: 15px;
            min-height: 120px;
            resize: vertical;
            font-size: 1rem;
            transition: all 0.3s ease;
        }

        .comment-input:focus {
            outline: none;
            border-color: var(--art-gold);
            box-shadow: 0 0 25px rgba(255, 209, 102, 0.4);
        }

        .comment-input::placeholder {
            color: var(--texto-secundario);
        }

        .comment-submit {
            background: linear-gradient(135deg, var(--destaque), var(--art-gold));
            color: white;
            padding: 12px 30px;
            border-radius: 25px;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            font-weight: 600;
            transition: all 0.3s ease;
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        .comment-submit:hover {
            transform: translateY(-2px);
            box-shadow: 0 8px 25px rgba(0, 180, 216, 0.4);
        }

        /* Animações */
        .fade-in {
            opacity: 0;
            animation: fadeIn 0.6s ease forwards;
        }

        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(30px); }
            to { opacity: 1; transform: translateY(0); }
        }

        /* Responsividade */
        @media (max-width: 768px) {
            .search-filter {
                flex-direction: column;
                gap: 15px;
            }

            .category-select {
                min-width: 100%;
            }

            .articles-grid {
                grid-template-columns: 1fr;
                gap: 20px;
            }

            .article-card {
                margin: 0 10px;
            }

            .page-title h2 {
                font-size: 2.2rem;
            }

            .logo h1 {
                font-size: 2rem;
            }

            .logo h1::after {
                right: -30px;
                font-size: 1.2rem;
            }

            nav a {
                margin-left: 15px;
                font-size: 0.9rem;
            }

            .modal-content {
                padding: 25px;
                width: 95%;
            }

            .modal-title {
                font-size: 1.8rem;
            }
        }

        @media (max-width: 480px) {
            .main-container {
                padding: 20px 3%;
            }

            .article-content {
                padding: 20px;
            }

            .article-title {
                font-size: 1.3rem;
            }

            .logo h1::after {
                display: none;
            }
        }

css/dashboard.css.content:
   :root {
            --roxo: #6a00ff;
            --azul: #00f7ff;
            --verde: #00ffaa;
            --fundo: #0f0c29;
            --fundo-card: #16213e;
            --fundo-nav: #0a081f;
            --texto: #ffffff;
            --texto-secundario: #cccccc;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Rajdhani', sans-serif;
            background: var(--fundo);
            color: var(--texto);
            min-height: 100vh;
        }

        /* Container Principal */
        .dashboard-container {
            max-width: 1400px;
            margin: 0 auto;
            padding: 20px;
        }

        /* Header */
        .dashboard-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 30px;
            padding-bottom: 20px;
            border-bottom: 1px solid rgba(0, 247, 255, 0.2);
            position: relative;
        }

        .logo h1 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2rem;
            background: linear-gradient(90deg, var(--roxo), var(--azul));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
        }

        .header-actions {
            display: flex;
            align-items: center;
            gap: 20px;
        }
/* Animação do Avatar */
.user-avatar {
    width: 50px;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, var(--roxo), var(--azul));
    color: white;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.5s ease-in-out;
}

.user-avatar i {
    transition: all 0.3s ease;
    font-size: 1.2rem;
}

.user-avatar.star {
    clip-path: polygon(
        50% 0%, 61% 35%, 98% 35%, 68% 57%, 
        79% 91%, 50% 70%, 21% 91%, 32% 57%, 
        2% 35%, 39% 35%
    );
}

.user-avatar.rectangle {
    width: 70px;
    border-radius: 10%;
}

/* Efeito de brilho no hover */
.user-avatar:hover {
    filter: drop-shadow(0 0 8px rgba(0, 247, 255, 0.7));
    transform: scale(1.1);
}

/* Efeito de rotação para formas */
@keyframes rotateAvatar {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.user-avatar.rotating {
    animation: rotateAvatar 4s linear infinite;
}
        .search-bar {
            position: relative;
            width: 300px;
        }

        .search-bar input {
            width: 100%;
            padding: 10px 15px 10px 40px;
            border-radius: 30px;
            border: none;
            background: rgba(10, 8, 31, 0.8);
            color: white;
            font-family: 'Rajdhani', sans-serif;
        }

        .search-bar i {
            position: absolute;
            left: 15px;
            top: 50%;
            transform: translateY(-50%);
            color: var(--azul);
        }

        .user-menu {
            display: flex;
            align-items: center;
            gap: 15px;
            position: relative;
        }

        .user-info {
            text-align: right;
        }

        .user-level {
            font-size: 0.8rem;
            color: var(--azul);
        }

        .user-avatar {
            width: 50px;
            height: 50px;
            border-radius: 50%;
            background: linear-gradient(135deg, var(--roxo), var(--azul));
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;
            font-weight: bold;
            font-size: 1.2rem;
            transition: transform 0.3s;
        }

        .user-avatar:hover {
            transform: scale(1.1);
        }

        .user-dropdown {
            position: absolute;
            top: 70px;
            right: 0;
            background: var(--fundo-card);
            border-radius: 10px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.3);
            width: 200px;
            overflow: hidden;
            display: none;
            z-index: 100;
        }

        .user-dropdown.show {
            display: block;
        }

        .user-dropdown a, .user-dropdown button {
            display: block;
            padding: 12px 15px;
            color: var(--texto);
            text-decoration: none;
            text-align: left;
            width: 100%;
            background: none;
            border: none;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            cursor: pointer;
            transition: background 0.3s;
        }

        .user-dropdown a:hover, .user-dropdown button:hover {
            background: rgba(0, 247, 255, 0.1);
        }

        .user-dropdown i {
            width: 25px;
            color: var(--verde);
        }

        /* Progresso do Usuário */
        .user-progress {
            margin-top: 5px;
            width: 100%;
        }

        .progress-bar-container {
            height: 6px;
            background: rgba(0, 247, 255, 0.1);
            border-radius: 3px;
            margin-top: 5px;
            overflow: hidden;
        }

        .progress-bar {
            height: 100%;
            background: linear-gradient(90deg, var(--verde), var(--azul));
            width: 65%;
            border-radius: 3px;
            transition: width 0.5s ease;
        }

        /* Destaque do Dia */
        .featured-card {
            grid-column: 1 / -1;
            display: flex;
            background: linear-gradient(135deg, #1a1a4a, #16213e);
            border-radius: 15px;
            overflow: hidden;
            box-shadow: 0 10px 20px rgba(0, 247, 255, 0.2);
            margin-bottom: 20px;
        }

        .featured-image {
            flex: 1;
            min-height: 250px;
            background-size: cover;
            background-position: center;
            position: relative;
        }

        .featured-image::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(to right, rgba(10, 8, 31, 0.9), transparent);
        }

        .featured-content {
            flex: 1;
            padding: 30px;
            display: flex;
            flex-direction: column;
            justify-content: center;
        }

        .featured-tag {
            display: inline-block;
            background: var(--verde);
            color: black;
            padding: 5px 15px;
            border-radius: 20px;
            font-weight: bold;
            margin-bottom: 15px;
        }

        .featured-title {
            font-family: 'Orbitron', sans-serif;
            font-size: 2rem;
            margin-bottom: 15px;
            background: linear-gradient(90deg, var(--verde), var(--azul));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
        }

        .featured-description {
            color: var(--texto-secundario);
            margin-bottom: 25px;
            line-height: 1.6;
        }

        .featured-button {
            display: inline-block;
            background: var(--roxo);
            color: white;
            padding: 12px 25px;
            border-radius: 30px;
            text-decoration: none;
            font-weight: bold;
            align-self: flex-start;
            transition: all 0.3s;
        }

        .featured-button:hover {
            background: var(--azul);
            transform: translateY(-3px);
            box-shadow: 0 5px 15px rgba(0, 247, 255, 0.4);
        }

        /* Grid de Cards */
        .cards-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
            gap: 25px;
            margin-top: 30px;
        }

        .dashboard-card {
            background: var(--fundo-card);
            border-radius: 15px;
            overflow: hidden;
            transition: all 0.3s cubic-bezier(0.25, 0.1, 0.25, 1.7);
            position: relative;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
        }

        .dashboard-card:hover {
            transform: translateY(-10px);
            box-shadow: 0 15px 30px rgba(0, 247, 255, 0.2);
        }

        .card-header {
            height: 150px;
            background-size: cover;
            background-position: center;
            position: relative;
        }

        .card-header::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(to bottom, rgba(0,0,0,0.1), rgba(10, 8, 31, 0.8));
        }

        .card-tag {
            position: absolute;
            top: 15px;
            left: 15px;
            background: rgba(0,0,0,0.7);
            color: white;
            padding: 5px 10px;
            border-radius: 20px;
            font-size: 0.7rem;
            z-index: 2;
        }

        .card-title {
            position: absolute;
            bottom: 20px;
            left: 20px;
            z-index: 2;
            font-family: 'Orbitron', sans-serif;
            font-size: 1.5rem;
            color: white;
        }

        .card-icon {
            position: absolute;
            top: 20px;
            right: 20px;
            z-index: 2;
            font-size: 2rem;
            color: var(--verde);
            background: rgba(0,0,0,0.5);
            width: 50px;
            height: 50px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .card-content {
            padding: 20px;
        }

        .card-description {
            color: var(--texto-secundario);
            margin-bottom: 20px;
            line-height: 1.5;
        }

        .card-stats {
            display: flex;
            justify-content: space-between;
            color: var(--texto-secundario);
            font-size: 0.9rem;
        }

        .card-button {
            display: block;
            text-align: center;
            background: var(--verde);
            color: black;
            padding: 12px;
            border-radius: 5px;
            text-decoration: none;
            font-weight: bold;
            margin-top: 15px;
            transition: all 0.3s;
        }

        .card-button:hover {
            background: var(--azul);
            transform: translateY(-2px);
            box-shadow: 0 5px 10px rgba(0, 247, 255, 0.3);
        }

        .favorite-btn {
            position: absolute;
            top: 15px;
            right: 80px;
            background: none;
            border: none;
            color: gold;
            font-size: 1.2rem;
            cursor: pointer;
            z-index: 3;
            opacity: 0.7;
            transition: all 0.3s;
        }

        .favorite-btn:hover {
            opacity: 1;
            transform: scale(1.2);
        }

        .favorite-btn.active {
            opacity: 1;
            color: gold;
            text-shadow: 0 0 10px gold;
        }

        /* Cores Temáticas para Cada Card */
