            line-height: 1.8;
            margin-bottom: 40px;
            color: var(--texto);
            font-size: 1.1rem;
        }

        .modal-text p {
            margin-bottom: 20px;
        }

        .comment-section {
            margin-top: 50px;
            border-top: 2px solid rgba(255, 7, 58, 0.3);
            padding-top: 30px;
        }

        .comment-title {
            color: var(--gaming-red);
            font-size: 1.6rem;
            margin-bottom: 25px;
            font-family: 'Orbitron', sans-serif;
        }

        .comment-form {
            margin-bottom: 35px;
        }

        .comment-input {
            width: 100%;
            padding: 20px;
            border-radius: 15px;
            border: 2px solid var(--gaming-red);
            background: rgba(10, 4, 60, 0.7);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            margin-bottom: 15px;
            min-height: 120px;
            resize: vertical;
            font-size: 1rem;
            transition: all 0.3s ease;
        }

        .comment-input:focus {
            outline: none;
            border-color: var(--gaming-electric);
            box-shadow: 0 0 25px rgba(0, 255, 255, 0.4);
        }

        .comment-input::placeholder {
            color: var(--texto-secundario);
        }

        .comment-submit {
            background: linear-gradient(135deg, var(--gaming-red), var(--gaming-electric));
            color: white;
            padding: 12px 30px;
            border-radius: 25px;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            font-weight: 600;
            transition: all 0.3s ease;
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        .comment-submit:hover {
            transform: translateY(-2px);
            box-shadow: 0 8px 25px rgba(255, 7, 58, 0.4);
        }

        /* Animações */
        .fade-in {
            opacity: 0;
            animation: fadeIn 0.6s ease forwards;
        }

        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(30px); }
            to { opacity: 1; transform: translateY(0); }
        }

        /* Responsividade */
        @media (max-width: 768px) {
            .search-filter {
                flex-direction: column;
                gap: 15px;
            }

            .category-select {
                min-width: 100%;
            }

            .articles-grid {
                grid-template-columns: 1fr;
                gap: 20px;
            }

            .article-card {
                margin: 0 10px;
            }

            .page-title h2 {
                font-size: 2.2rem;
            }

            .logo h1 {
                font-size: 2rem;
            }

            .logo h1::after {
                right: -30px;
                font-size: 1.2rem;
            }

            nav a {
                margin-left: 15px;
                font-size: 0.9rem;
            }

            .modal-content {
                padding: 25px;
                width: 95%;
            }

            .modal-title {
                font-size: 1.8rem;
            }
        }

        @media (max-width: 480px) {
            .main-container {
                padding: 20px 3%;
            }

