   :root {
            --roxo: #6a00ff;
            --azul: #00f7ff;
            --verde: #00ffaa;
            --destaque: #6a4c93;
            --fundo: #0a043c;
            --fundo-card: rgba(22, 33, 62, 0.85);
            --texto: #ffffff;
            --texto-secundario: rgba(255, 255, 255, 0.8);
            --borda: rgba(255, 255, 255, 0.1);
            --erro: #ff4757;
            --sucesso: var(--verde);
            --history-gold: #d4af37;
            --history-bronze: #cd7f32;
            --history-ancient: #8b4513;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Rajdhani', sans-serif;
            background: var(--fundo);
            color: var(--texto);
            line-height: 1.6;
            position: relative;
            overflow-x: hidden;
        }

        /* Fundo histórico animado */
        body::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: 
                radial-gradient(circle at 30% 20%, rgba(106, 76, 147, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 70% 80%, rgba(212, 175, 55, 0.12) 0%, transparent 50%),
                radial-gradient(circle at 20% 70%, rgba(205, 127, 50, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 80% 30%, rgba(139, 69, 19, 0.08) 0%, transparent 50%);
            z-index: -1;
            animation: historicalDrift 30s ease-in-out infinite;
        }

        @keyframes historicalDrift {
            0%, 100% { transform: scale(1) rotate(0deg); opacity: 0.8; }
            25% { transform: scale(1.1) rotate(90deg); opacity: 1; }
            50% { transform: scale(0.9) rotate(180deg); opacity: 0.9; }
            75% { transform: scale(1.05) rotate(270deg); opacity: 0.7; }
        }

        /* Partículas históricas */
        .history-particles {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 0;
        }

        .scroll-particle {
            position: absolute;
            width: 16px;
            height: 16px;
            background: var(--destaque);
            border-radius: 2px;
            opacity: 0;
            animation: ancientScroll var(--duration) infinite ease-in-out;
        }

        @keyframes ancientScroll {
            0%, 100% { 
                opacity: 0; 
                transform: translateY(100vh) rotate(0deg) scale(0.5); 
            }
            20% { 
                opacity: 0.8; 
                transform: translateY(80vh) rotate(45deg) scale(1); 
            }
            50% { 
                opacity: 1;
                transform: translateY(50vh) rotate(180deg) scale(1.1); 
            }
            80% { 
                opacity: 0.6; 
                transform: translateY(20vh) rotate(315deg) scale(1); 
            }
        }

        header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 20px 5%;
            background: linear-gradient(135deg, rgba(10, 4, 60, 0.95), rgba(106, 76, 147, 0.2));
            position: sticky;
            top: 0;
            z-index: 100;
            backdrop-filter: blur(15px);
            border-bottom: 2px solid var(--destaque);
            box-shadow: 0 4px 25px rgba(106, 76, 147, 0.3);
        }

        .logo h1 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.5rem;
            margin: 0;
            background: linear-gradient(90deg, var(--destaque), var(--history-gold), var(--history-bronze));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            text-shadow: 0 0 20px rgba(106, 76, 147, 0.5);
            letter-spacing: 2px;
            position: relative;
        }

        .logo h1::after {
            content: '📜';
            position: absolute;
            right: -40px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 1.5rem;
            animation: ancientScroll 5s ease-in-out infinite;
        }

        @keyframes ancientScroll {
            0%, 100% { transform: translateY(-50%) rotate(0deg); }
            25% { transform: translateY(-50%) rotate(-10deg) scale(1.1); }
            50% { transform: translateY(-50%) rotate(0deg) scale(1.2); }
            75% { transform: translateY(-50%) rotate(10deg) scale(1.1); }
        }

