        :root {
            --roxo: #6a00ff;
            --azul: #00f7ff;
            --verde: #00ffaa;
            --fundo: #0a043c;
            --fundo-card: rgba(22, 33, 62, 0.85);
            --texto: #ffffff;
            --texto-secundario: rgba(255, 255, 255, 0.8);
            --borda: rgba(255, 255, 255, 0.1);
            --erro: #ff4757;
            --sucesso: var(--verde);
            --misterio: #8e44ad;
            --paranormal: #e74c3c;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Rajdhani', sans-serif;
            background: var(--fundo);
            color: var(--texto);
            line-height: 1.6;
            position: relative;
            overflow-x: hidden;
        }

        /* Fundo animado místico */
        body::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: 
                radial-gradient(circle at 15% 25%, rgba(106, 0, 255, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 85% 75%, rgba(0, 247, 255, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 50% 50%, rgba(142, 68, 173, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 25% 75%, rgba(231, 76, 60, 0.1) 0%, transparent 50%);
            z-index: -1;
            animation: mysticFloat 20s ease-in-out infinite;
        }

        @keyframes mysticFloat {
            0%, 100% { transform: translate(0, 0) rotate(0deg); }
            33% { transform: translate(30px, -30px) rotate(120deg); }
            66% { transform: translate(-20px, 20px) rotate(240deg); }
        }

        /* Partículas místicas */
        .mystic-particles {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 0;
        }

        .particle {
            position: absolute;
            background: var(--roxo);
            border-radius: 50%;
            opacity: 0;
            animation: float var(--duration) infinite ease-in-out;
        }

        @keyframes float {
            0%, 100% { 
                opacity: 0; 
                transform: translateY(100vh) scale(0); 
            }
            10% { 
                opacity: 1; 
                transform: translateY(90vh) scale(1); 
            }
            90% { 
                opacity: 1; 
                transform: translateY(10vh) scale(1); 
            }
        }

        header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 20px 5%;
            background: linear-gradient(135deg, rgba(10, 4, 60, 0.95), rgba(142, 68, 173, 0.2));
            position: sticky;
            top: 0;
            z-index: 100;
            backdrop-filter: blur(15px);
            border-bottom: 2px solid var(--roxo);
            box-shadow: 0 4px 25px rgba(106, 0, 255, 0.3);
        }

        .logo h1 {
            font-family: 'Orbitron', sans-serif;
            font-size: 2.5rem;
            margin: 0;
            background: linear-gradient(90deg, var(--roxo), var(--misterio), var(--paranormal));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            text-shadow: 0 0 20px rgba(106, 0, 255, 0.5);
            letter-spacing: 2px;
            position: relative;
        }

        .logo h1::after {
            content: '🔮';
            position: absolute;
            right: -40px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 1.5rem;
            animation: glow 2s ease-in-out infinite alternate;
        }

        @keyframes glow {
            from { text-shadow: 0 0 10px var(--roxo); }
            to { text-shadow: 0 0 20px var(--roxo), 0 0 30px var(--misterio); }
        }

        .slogan {
            margin: 5px 0 0;
            font-size: 0.9rem;
            color: var(--azul);
            font-style: italic;
            max-width: 400px;
            line-height: 1.4;
        }

        nav a {
            color: var(--texto);
            text-decoration: none;
            margin-left: 25px;
            font-weight: 600;
            transition: all 0.3s ease;
            padding: 8px 16px;
            border-radius: 20px;
            position: relative;
        }

        nav a:hover {
            color: var(--roxo);
            background: rgba(106, 0, 255, 0.1);
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(106, 0, 255, 0.2);
        }

        nav a i {
            margin-right: 8px;
        }

        .main-container {
            padding: 40px 5%;
            max-width: 1400px;
            margin: 0 auto;
            position: relative;
            z-index: 1;
        }

        .page-title {
            text-align: center;
            margin-bottom: 50px;
