            font-family: 'Orbitron', sans-serif;
            font-size: 2.8rem;
            margin: 0;
            background: linear-gradient(45deg, var(--roxo), var(--misterio), var(--paranormal));
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            display: inline-block;
            position: relative;
            text-shadow: 0 0 30px rgba(106, 0, 255, 0.5);
        }

        .page-title h2::after {
            content: '';
            display: block;
            width: 150px;
            height: 4px;
            background: linear-gradient(90deg, var(--roxo), var(--misterio), var(--paranormal));
            margin: 15px auto 0;
            border-radius: 2px;
            box-shadow: 0 0 15px rgba(106, 0, 255, 0.5);
        }

        .search-filter {
            display: flex;
            justify-content: space-between;
            margin-bottom: 40px;
            gap: 20px;
            align-items: center;
        }

        .search-box {
            flex-grow: 1;
            position: relative;
        }

        .search-box input {
            width: 100%;
            padding: 15px 25px;
            border-radius: 30px;
            border: 2px solid var(--roxo);
            background: var(--fundo-card);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            font-size: 1.1rem;
            transition: all 0.3s ease;
            box-shadow: 0 4px 15px rgba(106, 0, 255, 0.2);
            backdrop-filter: blur(10px);
        }

        .search-box input:focus {
            outline: none;
            border-color: var(--misterio);
            box-shadow: 0 0 25px rgba(106, 0, 255, 0.4);
            background: rgba(22, 33, 62, 0.95);
        }

        .search-box input::placeholder {
            color: var(--texto-secundario);
