            border: 1px solid rgba(106, 0, 255, 0.3);
            transition: all 0.4s ease;
            position: relative;
            backdrop-filter: blur(15px);
        }

        .article-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(135deg, rgba(106, 0, 255, 0.05), rgba(142, 68, 173, 0.05));
            opacity: 0;
            transition: opacity 0.3s ease;
            border-radius: 20px;
        }

        .article-card:hover::before {
            opacity: 1;
