        .article-card:hover {
            transform: translateY(-10px) scale(1.02);
            box-shadow: 0 20px 40px rgba(106, 0, 255, 0.3);
            border-color: var(--roxo);
        }

        .article-image {
            height: 220px;
            background-size: cover;
            background-position: center;
            position: relative;
            overflow: hidden;
        }

        .article-image::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(to bottom, transparent 40%, rgba(10, 4, 60, 0.9));
            z-index: 2;
        }

        .article-category {
            position: absolute;
            top: 15px;
            right: 15px;
            background: linear-gradient(135deg, var(--roxo), var(--misterio));
            color: white;
            padding: 8px 16px;
            border-radius: 25px;
            font-size: 0.85rem;
            font-weight: 600;
            z-index: 3;
            text-transform: uppercase;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(106, 0, 255, 0.4);
        }

        .mystery-level {
            position: absolute;
            top: 15px;
            left: 15px;
            background: linear-gradient(135deg, var(--paranormal), var(--misterio));
            color: white;
            padding: 6px 12px;
            border-radius: 20px;
            font-size: 0.8rem;
            font-weight: 600;
            z-index: 3;
            display: flex;
            align-items: center;
            gap: 5px;
        }

        .article-content {
            padding: 25px;
            position: relative;
        }

        .article-title {
            font-size: 1.5rem;
            margin: 0 0 12px;
            color: var(--roxo);
            font-weight: 700;
            line-height: 1.3;
            font-family: 'Orbitron', sans-serif;
        }

        .article-author {
            font-size: 1rem;
            color: var(--azul);
            margin-bottom: 15px;
            display: block;
            font-weight: 600;
        }

        .article-excerpt {
            font-size: 1rem;
            margin-bottom: 20px;
            color: var(--texto-secundario);
            line-height: 1.6;
        }

        .article-meta {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 0.9rem;
            color: var(--texto-secundario);
            margin-bottom: 20px;
            border-top: 1px solid rgba(106, 0, 255, 0.2);
            padding-top: 15px;
        }

        .article-date {
            display: flex;
            align-items: center;
        }

        .article-date i {
            margin-right: 8px;
            color: var(--roxo);
        }

        .article-actions {
            display: flex;
            gap: 15px;
        }

        .action-btn {
            background: none;
            border: none;
            color: var(--texto-secundario);
            cursor: pointer;
            transition: all 0.3s ease;
            font-size: 1rem;
            display: flex;
            align-items: center;
            padding: 5px 10px;
            border-radius: 15px;
        }

        .action-btn i {
            margin-right: 5px;
        }

        .action-btn:hover {
            color: var(--roxo);
            background: rgba(106, 0, 255, 0.1);
            transform: scale(1.1);
        }

        .action-btn.liked {
            color: var(--paranormal);
        }

        .action-btn.favorited {
            color: var(--verde);
        }

        .read-more {
            display: inline-block;
            background: linear-gradient(135deg, var(--roxo), var(--misterio));
            color: white;
            padding: 12px 25px;
            border-radius: 25px;
            text-decoration: none;
            font-weight: 600;
            transition: all 0.3s ease;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            text-transform: uppercase;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(106, 0, 255, 0.3);
        }

        .read-more:hover {
            transform: translateY(-3px);
            box-shadow: 0 8px 25px rgba(106, 0, 255, 0.5);
            background: linear-gradient(135deg, var(--misterio), var(--paranormal));
        }

        .loading {
            text-align: center;
            padding: 40px;
            color: var(--roxo);
            font-size: 1.2rem;
        }

        .loading i {
            font-size: 2rem;
            margin-bottom: 10px;
            display: block;
        }

        .no-results {
            text-align: center;
            padding: 60px 20px;
            color: var(--texto-secundario);
        }

        .no-results i {
            font-size: 4rem;
            color: var(--roxo);
            margin-bottom: 20px;
            display: block;
        }

        .no-results h3 {
            color: var(--roxo);
            margin-bottom: 10px;
            font-family: 'Orbitron', sans-serif;
        }

        /* Modal */
        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.95);
            z-index: 1000;
            justify-content: center;
            align-items: center;
            backdrop-filter: blur(10px);
        }

        .modal-content {
            background: var(--fundo-card);
            border-radius: 25px;
            width: 90%;
            max-width: 900px;
            max-height: 90vh;
            overflow-y: auto;
            padding: 40px;
            position: relative;
            border: 2px solid var(--roxo);
            box-shadow: 0 0 50px rgba(106, 0, 255, 0.5);
            backdrop-filter: blur(20px);
        }

        .close-modal {
            position: absolute;
            top: 20px;
            right: 20px;
            background: none;
            border: none;
            color: var(--roxo);
            font-size: 2rem;
            cursor: pointer;
            transition: all 0.3s ease;
            width: 40px;
            height: 40px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .close-modal:hover {
            background: rgba(106, 0, 255, 0.2);
            transform: scale(1.1);
        }

        .modal-title {
            color: var(--roxo);
            font-size: 2.2rem;
            margin-bottom: 15px;
            font-family: 'Orbitron', sans-serif;
            line-height: 1.3;
        }

        .modal-author {
            color: var(--azul);
            font-size: 1.2rem;
            margin-bottom: 10px;
            display: block;
            font-weight: 600;
        }

        .modal-date {
            color: var(--texto-secundario);
            margin-bottom: 25px;
            display: block;
            font-size: 1rem;
        }

        .modal-image {
            width: 100%;
            height: 350px;
            background-size: cover;
            background-position: center;
            margin-bottom: 30px;
            border-radius: 15px;
            border: 2px solid rgba(106, 0, 255, 0.3);
        }

        .modal-text {
            line-height: 1.8;
            margin-bottom: 40px;
            color: var(--texto);
            font-size: 1.1rem;
        }

        .modal-text p {
            margin-bottom: 20px;
        }

        .comment-section {
            margin-top: 50px;
            border-top: 2px solid rgba(106, 0, 255, 0.3);
            padding-top: 30px;
        }

        .comment-title {
            color: var(--roxo);
            font-size: 1.6rem;
            margin-bottom: 25px;
            font-family: 'Orbitron', sans-serif;
        }

        .comment-form {
            margin-bottom: 35px;
        }

        .comment-input {
            width: 100%;
            padding: 20px;
            border-radius: 15px;
            border: 2px solid var(--roxo);
            background: rgba(10, 4, 60, 0.7);
            color: var(--texto);
            font-family: 'Rajdhani', sans-serif;
            margin-bottom: 15px;
            min-height: 120px;
            resize: vertical;
            font-size: 1rem;
            transition: all 0.3s ease;
        }

        .comment-input:focus {
            outline: none;
            border-color: var(--misterio);
            box-shadow: 0 0 25px rgba(106, 0, 255, 0.4);
        }

        .comment-input::placeholder {
            color: var(--texto-secundario);
        }

        .comment-submit {
            background: linear-gradient(135deg, var(--roxo), var(--misterio));
            color: white;
            padding: 12px 30px;
            border-radius: 25px;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            font-weight: 600;
            transition: all 0.3s ease;
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        .comment-submit:hover {
            transform: translateY(-2px);
            box-shadow: 0 8px 25px rgba(106, 0, 255, 0.4);
        }

        /* Animações */
        .fade-in {
            opacity: 0;
            animation: fadeIn 0.6s ease forwards;
        }

        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(30px); }
            to { opacity: 1; transform: translateY(0); }
        }

        /* Responsividade */
        @media (max-width: 768px) {
            .search-filter {
                flex-direction: column;
                gap: 15px;
            }

            .category-select {
                min-width: 100%;
            }

            .articles-grid {
                grid-template-columns: 1fr;
                gap: 20px;
            }

            .article-card {
                margin: 0 10px;
            }

            .page-title h2 {
                font-size: 2.2rem;
            }

            .logo h1 {
                font-size: 2rem;
            }

            .logo h1::after {
                right: -30px;
                font-size: 1.2rem;
            }

            nav a {
                margin-left: 15px;
                font-size: 0.9rem;
            }

            .modal-content {
                padding: 25px;
                width: 95%;
            }

            .modal-title {
                font-size: 1.8rem;
            }
        }

        @media (max-width: 480px) {
            .main-container {
                padding: 20px 3%;
            }

            .article-content {
                padding: 20px;
            }

            .article-title {
                font-size: 1.3rem;
            }

            .logo h1::after {
                display: none;
            }
        }

css/newsmania.css.content:
:root {
    --roxo: #6a00ff;
    --azul: #00f7ff;
    --verde: #00ffaa;
    --fundo: #0f0c29;
    --fundo-card: #16213e;
    --destaque: #ffbe0b;
    --perigo: #ff3d00;
    --sucesso: #00e676;
    --alerta: #ffc107;
    --texto-claro: #ffffff;
    --texto-escuro: #333333;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    background: linear-gradient(135deg, var(--fundo) 0%, #24243e 50%, var(--fundo) 100%);
    font-family: 'Rajdhani', sans-serif;
    color: var(--texto-claro);
    line-height: 1.6;
    min-height: 100vh;
}

/* Header */
header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 5%;
    background: linear-gradient(to right, rgba(15, 12, 41, 0.9), rgba(255, 190, 11, 0.1));
    position: sticky;
    top: 0;
    z-index: 100;
    backdrop-filter: blur(10px);
    border-bottom: 2px solid var(--destaque);
    box-shadow: 0 2px 20px rgba(255, 190, 11, 0.2);
}

.logo h1 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    background: linear-gradient(90deg, var(--destaque), #ff8c00);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 10px rgba(255, 190, 11, 0.5);
}

.slogan {
    margin: 0;
    font-size: 0.9rem;
    color: var(--azul);
    font-style: italic;
}

nav {
    display: flex;
    gap: 20px;
}

nav a {
    color: var(--texto-claro);
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s;
    padding: 8px 16px;
    border-radius: 20px;
    border: 1px solid transparent;
    cursor: pointer;
}

nav a:hover, nav a.active {
    color: var(--destaque);
    border-color: var(--destaque);
    background: rgba(255, 190, 11, 0.1);
}

nav a i {
    margin-right: 5px;
}

/* Main Container */
.main-container {
    padding: 0 5%;
    max-width: 1400px;
    margin: 0 auto;
}

/* Hero Section */
.hero-section {
    margin: 40px 0;
}

.hero-news {
    position: relative;
    height: 500px;
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 10px 40px rgba(255, 190, 11, 0.2);
    cursor: pointer;
    transition: all 0.3s;
}

.hero-news:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 50px rgba(255, 190, 11, 0.3);
}

.hero-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    background-color: var(--fundo-card);
}

.hero-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, rgba(0, 0, 0, 0.7) 0%, rgba(0, 0, 0, 0.3) 100%);
}

.hero-content {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 40px;
    z-index: 2;
}

.hero-category {
    display: inline-block;
    background: var(--destaque);
    color: var(--texto-escuro);
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.9rem;
    font-weight: 600;
    margin-bottom: 15px;
    text-transform: uppercase;
}

.hero-title {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 15px;
    color: var(--texto-claro);
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
}

.hero-excerpt {
    font-size: 1.2rem;
    margin-bottom: 20px;
    color: #ddd;
    max-width: 80%;
}

.hero-meta {
    display: flex;
    gap: 20px;
    font-size: 0.9rem;
    color: #bbb;
}

.hero-source {
    font-weight: 600;
    color: var(--destaque);
}

/* Filters Section */
.filters-section {
    margin: 40px 0;
}

.search-filter {
    display: flex;
    gap: 20px;
    align-items: center;
    background: var(--fundo-card);
    padding: 20px;
    border-radius: 15px;
    border: 1px solid rgba(255, 190, 11, 0.3);
}

.search-box {
    flex: 1;
    position: relative;
}

.search-box input {
    width: 100%;
    padding: 12px 50px 12px 20px;
    border-radius: 25px;
    border: 2px solid var(--destaque);
    background: rgba(0, 0, 0, 0.3);
    color: var(--texto-claro);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    transition: all 0.3s;
}

.search-box input:focus {
    outline: none;
    box-shadow: 0 0 15px rgba(255, 190, 11, 0.3);
}

.search-box button {
    position: absolute;
    right: 5px;
    top: 50%;
    transform: translateY(-50%);
    background: var(--destaque);
    color: var(--texto-escuro);
    border: none;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    cursor: pointer;
    transition: all 0.3s;
}

.search-box button:hover {
    background: #e6a800;
    transform: translateY(-50%) scale(1.1);
}

.filter-controls {
    display: flex;
    gap: 15px;
    align-items: center;
}

.filter-controls select {
    padding: 12px 15px;
    border-radius: 25px;
    border: 2px solid var(--destaque);
    background: rgba(0, 0, 0, 0.3);
    color: var(--texto-claro);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    min-width: 150px;
}

.filter-controls select option {
    background: var(--fundo-card);
    color: var(--texto-claro);
}

#refreshBtn {
    background: linear-gradient(45deg, var(--destaque), #ff8c00);
    color: var(--texto-escuro);
    border: none;
    padding: 12px 20px;
    border-radius: 25px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s;
    font-family: 'Rajdhani', sans-serif;
}

#refreshBtn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 190, 11, 0.4);
}

/* News Section */
.news-section {
    margin: 40px 0;
}

.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
}

.section-header h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2rem;
    color: var(--destaque);
}

.loading-indicator {
    color: var(--destaque);
    font-weight: 600;
    display: none;
}

.loading-indicator.active {
    display: block;
}

.news-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
    gap: 30px;
    margin-bottom: 40px;
}

.news-card {
    background: var(--fundo-card);
    border-radius: 15px;
    overflow: hidden;
    border: 1px solid rgba(255, 190, 11, 0.3);
    transition: all 0.3s;
    cursor: pointer;
    position: relative;
    opacity: 0;
    transform: translateY(20px);
}

.news-card.fade-in {
    opacity: 1;
    transform: translateY(0);
}

.news-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(255, 190, 11, 0.2);
    border-color: var(--destaque);
}

.news-image {
    height: 200px;
    background-size: cover;
    background-position: center;
    background-color: #333;
    position: relative;
}

.news-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.3));
}

.news-category {
    position: absolute;
    top: 15px;
    left: 15px;
    background: var(--destaque);
    color: var(--texto-escuro);
    padding: 5px 12px;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 600;
    text-transform: uppercase;
    z-index: 1;
}

.trending-badge {
    position: absolute;
    top: 15px;
    right: 15px;
    background: var(--perigo);
    color: white;
    padding: 5px 10px;
    border-radius: 15px;
    font-size: 0.7rem;
    font-weight: 600;
    animation: pulse 2s infinite;
}

.breaking-badge {
    position: absolute;
    top: 50px;
    right: 15px;
    background: var(--alerta);
    color: var(--texto-escuro);
    padding: 5px 10px;
    border-radius: 15px;
    font-size: 0.7rem;
    font-weight: 600;
    animation: pulse 2s infinite;
}

.country-flag {
    position: absolute;
    bottom: 15px;
    left: 15px;
    background: rgba(0, 0, 0, 0.7);
    color: white;
    padding: 5px 10px;
    border-radius: 10px;
    font-size: 0.8rem;
    z-index: 1;
}

.news-content {
    padding: 20px;
}

.news-title {
    font-size: 1.2rem;
    font-weight: 700;
    margin-bottom: 10px;
    color: var(--texto-claro);
    line-height: 1.4;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.news-description {
    font-size: 0.95rem;
    color: #ddd;
    margin-bottom: 15px;
    line-height: 1.5;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.news-meta {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.85rem;
    color: #aaa;
    border-top: 1px solid rgba(255, 190, 11, 0.2);
    padding-top: 15px;
}

.news-source {
    font-weight: 600;
    color: var(--destaque);
}

.news-date {
    display: flex;
    align-items: center;
}

.news-date i {
    margin-right: 5px;
}

.news-actions {
    position: absolute;
    bottom: 15px;
    right: 15px;
    display: flex;
    gap: 10px;
}

.news-actions button {
    background: rgba(0, 0, 0, 0.7);
    border: none;
    color: white;
    width: 30px;
    height: 30px;
    border-radius: 50%;
    cursor: pointer;
    transition: all 0.3s;
}

.news-actions button:hover {
    background: var(--destaque);
    color: var(--texto-escuro);
    transform: scale(1.1);
}

.news-actions button.saved {
    background: var(--destaque);
    color: var(--texto-escuro);
}

/* Load More Button */
.load-more-container {
    text-align: center;
    margin: 40px 0;
}

.load-more-btn {
    background: linear-gradient(45deg, var(--destaque), #ff8c00);
    color: var(--texto-escuro);
    border: none;
    padding: 15px 30px;
    border-radius: 30px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s;
    font-family: 'Rajdhani', sans-serif;
}

.load-more-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(255, 190, 11, 0.4);
}

.load-more-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

/* Trending Section */
.trending-section {
    margin: 60px 0;
    background: var(--fundo-card);
    padding: 30px;
    border-radius: 20px;
    border: 1px solid rgba(255, 190, 11, 0.3);
}

.trending-section h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.8rem;
    color: var(--destaque);
    margin-bottom: 30px;
    text-align: center;
}

.trending-news {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 20px;
}

.trending-card {
    display: flex;
    background: rgba(0, 0, 0, 0.3);
    border-radius: 10px;
    overflow: hidden;
    border: 1px solid rgba(255, 190, 11, 0.2);
    transition: all 0.3s;
    cursor: pointer;
}

.trending-card:hover {
    border-color: var(--destaque);
    transform: translateX(5px);
}

.trending-image {
    width: 80px;
    height: 80px;
    background-size: cover;
    background-position: center;
    background-color: #333;
    flex-shrink: 0;
}

.trending-content {
    padding: 15px;
    flex: 1;
}

.trending-title {
    font-size: 0.9rem;
    font-weight: 600;
    color: var(--texto-claro);
    line-height: 1.3;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    margin-bottom: 5px;
}

.trending-source {
    font-size: 0.8rem;
    color: var(--destaque);
    font-weight: 600;
}

/* Modal */
.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    z-index: 1000;
    justify-content: center;
    align-items: flex-start;
    padding: 20px;
    overflow-y: auto;
}

.modal.active {
    display: flex;
}

.modal-content {
    background: var(--fundo-card);
    border-radius: 20px;
    width: 100%;
    max-width: 800px;
    max-height: 90vh;
    overflow-y: auto;
    position: relative;
    border: 2px solid var(--destaque);
    box-shadow: 0 0 40px rgba(255, 190, 11, 0.3);
    margin-top: 50px;
}

.close-modal {
    position: absolute;
    top: 20px;
    right: 20px;
    background: rgba(0, 0, 0, 0.5);
    border: none;
    color: var(--destaque);
    font-size: 1.5rem;
    cursor: pointer;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1;
    transition: all 0.3s;
}

.close-modal:hover {
    background: var(--destaque);
    color: var(--texto-escuro);
}

.modal-header {
    position: relative;
}

.modal-image {
    width: 100%;
    height: 300px;
    object-fit: cover;
    border-radius: 20px 20px 0 0;
    background-color: #333;
}

.modal-info {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
    padding: 40px 30px 30px;
    color: var(--texto-claro);
}

.modal-category {
    display: inline-block;
    background: var(--destaque);
    color: var(--texto-escuro);
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.9rem;
    font-weight: 600;
    margin-bottom: 15px;
    text-transform: uppercase;
}

.modal-title {
    font-size: 1.8rem;
    font-weight: 700;
    margin-bottom: 15px;
    line-height: 1.3;
}

.modal-meta {
    display: flex;
    gap: 20px;
    font-size: 0.9rem;
    color: #ddd;
    flex-wrap: wrap;
}

.modal-source {
    font-weight: 600;
    color: var(--destaque);
}

.modal-body {
    padding: 30px;
}

.modal-description {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 30px;
    color: #ddd;
}

.modal-actions {
    display: flex;
    gap: 15px;
    flex-wrap: wrap;
}

.action-btn {
    background: rgba(255, 190, 11, 0.1);
    color: var(--destaque);
    border: 2px solid var(--destaque);
    padding: 12px 20px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    display: flex;
    align-items: center;
    gap: 8px;
}

.action-btn:hover {
    background: var(--destaque);
    color: var(--texto-escuro);
    transform: translateY(-2px);
}

/* Toast */
.toast {
    position: fixed;
    bottom: 30px;
    right: 30px;
    background: var(--fundo-card);
    color: var(--texto-claro);
    padding: 15px 20px;
    border-radius: 10px;
    border: 1px solid var(--destaque);
    box-shadow: 0 5px 20px rgba(255, 190, 11, 0.3);
    transform: translateX(400px);
    transition: all 0.3s;
    z-index: 1001;
    max-width: 300px;
    display: none;
}

.toast.active {
    transform: translateX(0);
    display: block;
}

.toast-content {
    display: flex;
    align-items: center;
    gap: 10px;
}

.toast-icon {
    color: var(--destaque);
    font-size: 1.2rem;
}

/* Footer */
footer {
    background: var(--fundo-card);
    text-align: center;
    padding: 30px 5%;
    border-top: 1px solid rgba(255, 190, 11, 0.3);
    margin-top: 60px;
}

.footer-content p {
    margin: 5px 0;
    color: #aaa;
}

/* Responsive Design */
@media (max-width: 768px) {
    .main-container {
        padding: 0 3%;
    }
    
    header {
        flex-direction: column;
        gap: 20px;
        padding: 20px 3%;
    }
    
    nav {
        flex-wrap: wrap;
        justify-content: center;
    }
    
    .hero-title {
        font-size: 2rem;
    }
    
    .hero-excerpt {
        max-width: 100%;
    }
    
    .search-filter {
        flex-direction: column;
        gap: 15px;
    }
    
    .filter-controls {
        width: 100%;
        flex-wrap: wrap;
    }
    
    .filter-controls select {
        min-width: 120px;
        flex: 1;
    }
    
    .news-grid {
        grid-template-columns: 1fr;
    }
    
    .trending-news {
        grid-template-columns: 1fr;
    }
    
    .modal-content {
        margin-top: 20px;
    }
    
    .modal-actions {
        justify-content: center;
    }
    
    .toast {
        right: 15px;
        left: 15px;
        max-width: none;
    }
}

/* Animações */
@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
}

.fade-in {
    animation: fadeIn 0.5s ease-out;
}

css/paginainicial.css.content:
:root {
    --roxo: #6a00ff;
    --azul: #00f7ff;
    --verde: #00ffaa;
    --fundo: #0f0c29;
    --fundo-card: #16213e;
    --destaque: #ff00aa;
}

/* Reset e Estilos Base */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    margin: 0;
    padding: 0;
    background: var(--fundo);
    font-family: 'Rajdhani', sans-serif;
    color: white;
    line-height: 1.6;
    overflow-x: hidden;
}

/* Header Estilizado */
header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 5%;
    background: linear-gradient(to right, rgba(15, 12, 41, 0.9), rgba(106, 0, 255, 0.7));
    position: sticky;
    top: 0;
    z-index: 100;
    backdrop-filter: blur(8px);
    border-bottom: 1px solid var(--azul);
}

.logo h1 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    background: linear-gradient(90deg, var(--roxo), var(--azul));
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 10px rgba(106, 0, 255, 0.5);
    animation: neon-glow 3s infinite alternate;
}

.slogan {
    margin: 0;
    font-size: 0.9rem;
    color: var(--azul);
    font-weight: bold;
}

nav {
    display: flex;
    align-items: center;
}

nav a {
    color: white;
    text-decoration: none;
    margin-left: 25px;
    font-weight: bold;
    transition: all 0.3s;
    padding: 8px 15px;
    border-radius: 20px;
}

nav a:hover {
    color: var(--verde);
    background: rgba(0, 247, 255, 0.1);
    transform: translateY(-2px);
}

/* Estilos do botão de apps e minipágina */
.apps-container {
    width: 280px;
    background-color: rgba(21, 33, 62, 0.95);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border-radius: 12px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
    position: fixed;
    top: 80px;
    right: 20px;
    z-index: 1000;
    overflow: hidden;
    border: 1px solid var(--azul);
    display: none;
}

.apps-header {
    padding: 16px;
    border-bottom: 1px solid rgba(0, 247, 255, 0.2);
    background: rgba(106, 0, 255, 0.2);
}

.apps-title {
    font-size: 16px;
    font-weight: 600;
    color: var(--azul);
    font-family: 'Orbitron', sans-serif;
    text-align: center;
}

.apps-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 12px;
    padding: 16px;
    background: rgba(15, 12, 41, 0.7);
}

.app-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 12px 8px;
    border-radius: 8px;
    cursor: pointer;
    transition: all 0.2s ease;
    text-decoration: none;
    background: rgba(0, 247, 255, 0.05);
    border: 1px solid rgba(0, 247, 255, 0.1);
}

.app-item:hover {
    background: rgba(0, 247, 255, 0.15);
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0, 247, 255, 0.2);
}

.app-icon {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    margin-bottom: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--azul);
}

.app-icon img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.app-name {
    font-size: 13px;
    color: white;
    text-align: center;
    line-height: 1.4;
    font-weight: 500;
}

.apps-footer {
    padding: 12px 16px;
    border-top: 1px solid rgba(0, 247, 255, 0.2);
    background: rgba(106, 0, 255, 0.2);
    text-align: center;
}

.more-apps {
    color: var(--azul);
    text-decoration: none;
    font-size: 14px;
    font-weight: 500;
    display: inline-block;
    padding: 4px 8px;
    border-radius: 4px;
    transition: all 0.3s;
}

.more-apps:hover {
    color: var(--verde);
    background: rgba(0, 255, 170, 0.1);
}

.apps-menu-button {
    background: none;
    border: none;
    cursor: pointer;
    padding: 8px;
    margin-left: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgba(26, 115, 232, 0.1);
    border-radius: 50%;
    width: 40px;
    height: 40px;
    transition: all 0.3s;
}

.apps-menu-button svg {
    fill: var(--azul);
    transition: fill 0.2s;
}

.apps-menu-button:hover {
    background-color: rgba(0, 247, 255, 0.2);
}

.apps-menu-button:hover svg {
    fill: var(--verde);
}

/* Hero Section */
.hero {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 100px 5%;
    background: radial-gradient(circle at 70% 30%, #1a1a4a, var(--fundo));
    position: relative;
    overflow: hidden;
}

.hero::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(0, 247, 255, 0.1) 0%, transparent 70%);
    animation: rotate 20s linear infinite;
    z-index: 0;
}

@keyframes rotate {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.hero-texto {
    position: relative;
    z-index: 2;
    max-width: 600px;
}

.hero-texto h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 3.5rem;
    margin-bottom: 20px;
    line-height: 1.2;
}

.hero-texto h2 span {
    color: var(--destaque);
    text-shadow: 0 0 15px var(--destaque);
}

.hero-texto p {
    font-size: 1.3rem;
    margin-bottom: 30px;
    color: #ddd;
}

.btn-explorar {
    display: inline-block;
    text-decoration: none;
    text-align: center;
    background: var(--verde);
    color: black;
    border: none;
    padding: 15px 30px;
    border-radius: 30px;
    font-weight: bold;
    font-size: 1.2rem;
    cursor: pointer;
    transition: all 0.3s;
    position: relative;
    overflow: hidden;
    z-index: 1;
    font-family: 'Orbitron', sans-serif;
}

.btn-explorar::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: 0.5s;
    z-index: -1;
}

.btn-explorar:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 255, 170, 0.6);
    color: black;
}

.btn-explorar:hover::before {
    left: 100%;
}

.hero-imagem {
    position: relative;
    z-index: 2;
    filter: drop-shadow(0 0 30px rgba(106, 0, 255, 0.7));
}

.hero-imagem img {
    max-width: 450px;
    width: 100%;
    height: auto;
    animation: flutuar 6s ease-in-out infinite;
    border-radius: 20px;
    border: 2px solid var(--azul);
    box-shadow: 0 0 50px rgba(0, 247, 255, 0.4);
}

@keyframes flutuar {
    0%, 100% { transform: translateY(0) rotate(-2deg); }
    50% { transform: translateY(-20px) rotate(2deg); }
}

/* Seção de Curiosidades */
.titulo-secao {
    text-align: center;
    font-family: 'Orbitron', sans-serif;
    font-size: 3rem;
    margin: 80px 0 50px;
    color: var(--azul);
    position: relative;
    display: inline-block;
    width: 100%;
}

.titulo-secao::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 4px;
    background: var(--verde);
    border-radius: 2px;
}

.container-cards {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    gap: 40px;
    padding: 0 5% 100px;
    max-width: 1400px;
    margin: 0 auto;
}

.card {
    background: var(--fundo-card);
    border-radius: 15px;
    overflow: hidden;
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    cursor: pointer;
    height: 450px;
    perspective: 1000px;
    position: relative;
    border: 1px solid rgba(0, 247, 255, 0.2);
}

.card:hover {
    transform: translateY(-15px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 247, 255, 0.2);
    border-color: var(--azul);
}

.card-categoria {
    position: absolute;
    top: 15px;
    right: 15px;
    background: var(--roxo);
    color: white;
    padding: 5px 15px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: bold;
    z-index: 3;
}

.card-inner {
    position: relative;
    width: 100%;
    height: 100%;
    transition: transform 0.8s;
    transform-style: preserve-3d;
}

.card.flipped .card-inner {
    transform: rotateY(180deg);
}

.card-frente, .card-verso {
    position: absolute;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
    padding: 30px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.card-frente {
    background: linear-gradient(135deg, var(--fundo-card), #1a1a4a);
    align-items: center;
    text-align: center;
}

.card-verso {
    background: linear-gradient(135deg, #1a1a4a, var(--fundo-card));
    transform: rotateY(180deg);
    justify-content: space-between;
}

.card h3 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2rem;
    margin-top: 0;
    color: var(--verde);
    text-shadow: 0 0 10px rgba(0, 255, 170, 0.3);
}

.card img {
    width: 100%;
    max-height: 220px;
    object-fit: cover;
    border-radius: 10px;
    margin: 20px 0;
    border: 1px solid rgba(0, 247, 255, 0.3);
    box-shadow: 0 5px 15px rgba(0,0,0,0.3);
}

.card p {
    font-size: 1.1rem;
    line-height: 1.6;
    color: #ddd;
}

.btn-saiba-mais {
    background: var(--roxo);
    color: white;
    border: none;
    padding: 12px 25px;
    border-radius: 5px;
    font-weight: bold;
    cursor: pointer;
    align-self: flex-end;
    transition: all 0.3s;
    display: flex;
    align-items: center;
    gap: 8px;
    font-family: 'Rajdhani', sans-serif;
}

.btn-saiba-mais:hover {
    background: #7d2aff;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(106, 0, 255, 0.4);
}

/* Seção de Funcionalidades */
.funcionalidades {
    padding: 80px 5%;
    background: linear-gradient(to bottom, rgba(22, 33, 62, 0.8), rgba(10, 8, 31, 0.9));
    position: relative;
    overflow: hidden;
}

.funcionalidades::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 20% 80%, rgba(0, 247, 255, 0.05) 0%, transparent 50%),
                radial-gradient(circle at 80% 20%, rgba(106, 0, 255, 0.05) 0%, transparent 50%);
    pointer-events: none;
}

.funcionalidades-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 30px;
    max-width: 1200px;
    margin: 0 auto;
    position: relative;
    z-index: 2;
}

.funcionalidade-item {
    background: var(--fundo-card);
    border-radius: 15px;
    padding: 30px;
    text-decoration: none;
    color: white;
    transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    border: 2px solid rgba(0, 247, 255, 0.2);
    position: relative;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    min-height: 250px;
}

.funcionalidade-item::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 247, 255, 0.1), transparent);
    transition: left 0.5s;
}

.funcionalidade-item:hover::before {
    left: 100%;
}

.funcionalidade-item:hover {
    transform: translateY(-10px) scale(1.02);
    border-color: var(--azul);
    box-shadow: 0 20px 40px rgba(0, 247, 255, 0.3);
}

.funcionalidade-icon {
    font-size: 4rem;
    margin-bottom: 20px;
    filter: drop-shadow(0 0 10px rgba(0, 247, 255, 0.5));
}

.funcionalidade-item h3 {
    font-family: 'Orbitron', sans-serif;
    color: var(--azul);
    font-size: 1.5rem;
    margin-bottom: 15px;
}

.funcionalidade-item p {
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.6;
    margin-bottom: 20px;
    flex-grow: 1;
}

.funcionalidade-arrow {
    font-size: 1.5rem;
    color: var(--verde);
    font-weight: bold;
    transition: transform 0.3s;
}

.funcionalidade-item:hover .funcionalidade-arrow {
    transform: translateX(10px);
}

/* Seção de Mais Curiosidades */
.mais-curiosidades {
    padding: 100px 5%;
    background: linear-gradient(to bottom, rgba(22, 33, 62, 0.8), rgba(10, 8, 31, 0.9));
    position: relative;
    overflow: hidden;
}

.mais-curiosidades::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" preserveAspectRatio="none"><path fill="rgba(0,247,255,0.03)" d="M0,0 L100,0 L100,100 L0,100 Z" /></svg>');
    background-size: 100px 100px;
    opacity: 0.3;
}

.mais-curiosidades h2 {
    text-align: center;
    margin-bottom: 50px;
    color: var(--azul);
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    position: relative;
    z-index: 2;
}

.curiosidades-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 25px;
    max-width: 1200px;
    margin: 0 auto;
    position: relative;
    z-index: 2;
}

.curiosidade-item {
    background: var(--fundo-card);
    border-radius: 15px;
    padding: 25px;
    border: 2px solid rgba(0, 247, 255, 0.2);
    transition: all 0.4s ease;
    position: relative;
    overflow: hidden;
}

.curiosidade-item:hover {
    transform: translateY(-5px);
    border-color: var(--azul);
    box-shadow: 0 15px 30px rgba(0, 247, 255, 0.2);
}

.curiosidade-categoria {
    display: inline-block;
    padding: 6px 15px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: bold;
    margin-bottom: 15px;
}

.biologia {
    background-color: rgba(76, 175, 80, 0.2);
    color: #4caf50;
    border: 1px solid #4caf50;
}

.fisica {
    background-color: rgba(33, 150, 243, 0.2);
    color: #2196f3;
    border: 1px solid #2196f3;
}

.astronomia {
    background-color: rgba(156, 39, 176, 0.2);
    color: #9c27b0;
    border: 1px solid #9c27b0;
}

.quimica {
    background-color: rgba(255, 152, 0, 0.2);
    color: #ff9800;
    border: 1px solid #ff9800;
}

.curiosidade-titulo {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.4rem;
    margin-bottom: 15px;
    color: var(--azul);
}

.curiosidade-descricao {
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.6;
    margin-bottom: 20px;
}

.saiba-mais-btn {
    display: inline-block;
    margin-top: 15px;
    padding: 10px 20px;
    background: linear-gradient(45deg, var(--roxo), var(--azul));
    color: white;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s;
    border: none;
    cursor: pointer;
}

.saiba-mais-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(106, 0, 255, 0.4);
}

/* Animações */
@keyframes float {
    0% { transform: translateY(0) translateX(0) rotate(0deg); opacity: 0.1; }
    50% { transform: translateY(-80px) translateX(20px) rotate(180deg); opacity: 0.6; }
    100% { transform: translateY(0) translateX(0) rotate(360deg); opacity: 0.1; }
}

@keyframes neon-glow {
    0%, 100% { text-shadow: 0 0 5px var(--azul), 0 0 10px var(--azul), 0 0 20px var(--azul); }
    50% { text-shadow: 0 0 10px var(--azul), 0 0 20px var(--azul), 0 0 30px var(--azul); }
}

/* Efeitos para cards e itens */
.curiosidade-item, .card {
    --mouse-x: 50%;
    --mouse-y: 50%;
}

.curiosidade-item::after, .card::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at var(--mouse-x) var(--mouse-y), rgba(0, 247, 255, 0.1), transparent 70%);
    opacity: 0;
    transition: opacity 0.3s;
    z-index: -1;
    pointer-events: none;
}

.curiosidade-item:hover::after, .card:hover::after {
    opacity: 1;
}

/* Animações ao aparecer na tela */
.curiosidade-item, .card {
    opacity: 0;
    transform: translateY(30px);
    transition: opacity 0.6s ease-out, transform 0.6s ease-out;
}

.curiosidade-item.animate, .card.animate {
    opacity: 1;
    transform: translateY(0);
}

/* Tema claro (opcional) */
.light-theme {
    --fundo: #f0f2f5;
    --fundo-card: #ffffff;
    --roxo: #7b2aff;
    --azul: #0066ff;
    --verde: #00c853;
    --destaque: #ff4081;
    color: #333;
}

.light-theme .card,
.light-theme .curiosidade-item,
.light-theme .funcionalidade-item {
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

.light-theme .card p,
.light-theme .curiosidade-descricao,
.light-theme .funcionalidade-item p {
    color: #555;
}

/* Responsividade */
@media (max-width: 768px) {
    .hero {
        flex-direction: column;
        text-align: center;
        padding: 60px 5%;
    }
    
    .hero-texto h2 {
        font-size: 2.5rem;
    }
    
    .titulo-secao {
        font-size: 2.5rem;
    }
    
    .container-cards {
        grid-template-columns: 1fr;
        gap: 30px;
        padding: 0 3% 80px;
    }
    
    .funcionalidades-grid {
        grid-template-columns: 1fr;
        gap: 20px;
    }
    
    .curiosidades-grid {
        grid-template-columns: 1fr;
        gap: 20px;
    }
    
    nav a {
        margin-left: 15px;
        font-size: 0.9rem;
    }
    
    .logo h1 {
        font-size: 2rem;
    }
    
    .apps-container {
        width: 260px;
        right: 10px;
    }
}

@media (max-width: 480px) {
    .hero-texto h2 {
        font-size: 2rem;
    }
    
    .titulo-secao {
        font-size: 2rem;
    }
    
    .card {
        height: 400px;
    }
    
    .funcionalidade-item {
        min-height: 200px;
        padding: 20px;
    }
    
    .curiosidade-item {
        padding: 20px;
    }
    
    .logo h1 {
        font-size: 1.8rem;
    }
    
    nav a {
        margin-left: 10px;
        padding: 6px 12px;
        font-size: 0.8rem;
    }
    
    .apps-container {
        width: 240px;
        right: 5px;
    }
    
    .hero {
        padding: 40px 3%;
    }
    
    .hero-texto p {
        font-size: 1.1rem;
    }
    
    .btn-explorar {
        padding: 12px 25px;
        font-size: 1rem;
    }
}

/* Scrollbar personalizada */
::-webkit-scrollbar {
    width: 10px;
}

::-webkit-scrollbar-track {
    background: var(--fundo);
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(to bottom, var(--roxo), var(--azul));
    border-radius: 5px;
}

::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(to bottom, var(--azul), var(--verde));
}

/* Seleção de texto */
::selection {
    background: var(--azul);
    color: white;
}

::-moz-selection {
    background: var(--azul);
    color: white;
}

css/quizmania.css.content:
:root {
    --roxo: #6a00ff;
    --azul: #00f7ff;
    --verde: #00ffaa;
    --fundo: #0f0c29;
    --fundo-card: #16213e;
    --destaque: #ff6b6b;
    --perigo: #ff3d00;
    --sucesso: #00e676;
    --alerta: #ffc107;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    background: var(--fundo);
    font-family: 'Rajdhani', sans-serif;
    color: white;
    line-height: 1.6;
    min-height: 100vh;
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 5%;
    background: linear-gradient(to right, rgba(15, 12, 41, 0.9), rgba(255, 107, 107, 0.7));
    position: sticky;
    top: 0;
    z-index: 100;
    backdrop-filter: blur(8px);
    border-bottom: 2px solid var(--destaque);
}

.logo h1 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    background: linear-gradient(90deg, var(--destaque), #ff8a8a);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 10px rgba(255, 107, 107, 0.5);
}

.slogan {
    margin: 0;
    font-size: 0.9rem;
    color: var(--destaque);
    font-style: italic;
    max-width: 400px;
}

nav a {
    color: white;
    text-decoration: none;
    margin-left: 20px;
    font-weight: 600;
    transition: color 0.3s;
}

nav a:hover {
    color: var(--destaque);
}

nav a i {
    margin-right: 5px;
}

.main-container {
    padding: 40px 5%;
}

.page-title {
    text-align: center;
    margin-bottom: 40px;
    position: relative;
}

.page-title h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.2rem;
    margin: 0;
    color: var(--destaque);
    display: inline-block;
}

.page-title h2::after {
    content: '';
    display: block;
    width: 100px;
    height: 3px;
    background: linear-gradient(to right, var(--destaque), #ff8a8a);
    margin: 10px auto 0;
}

.articles-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 30px;
}

.article-card {
    background: var(--fundo-card);
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(255, 107, 107, 0.3);
    transition: transform 0.3s, box-shadow 0.3s;
    position: relative;
    cursor: pointer;
}

.article-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(255, 107, 107, 0.4);
    border-color: var(--destaque);
}

.article-image {
    height: 180px;
    background-size: cover;
    background-position: center;
    position: relative;
}

.article-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.7));
}

.article-category {
    position: absolute;
    top: 15px;
    right: 15px;
    background: rgba(255, 107, 107, 0.8);
    color: white;
    padding: 5px 10px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    z-index: 1;
}

.quiz-difficulty {
    position: absolute;
    top: 15px;
    left: 15px;
    background: rgba(0, 0, 0, 0.8);
    color: white;
    padding: 5px 10px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    z-index: 1;
}

.quiz-difficulty.easy { border: 2px solid var(--sucesso); }
.quiz-difficulty.medium { border: 2px solid var(--alerta); }
.quiz-difficulty.hard { border: 2px solid var(--perigo); }

.article-content {
    padding: 20px;
}

.article-title {
    font-size: 1.3rem;
    margin: 0 0 10px;
    color: var(--destaque);
    font-weight: 700;
}

.article-author {
    font-size: 0.9rem;
    color: var(--azul);
    margin-bottom: 15px;
    display: block;
}

.article-excerpt {
    font-size: 0.95rem;
    margin-bottom: 20px;
    color: #ddd;
    line-height: 1.5;
}

.quiz-stats {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.85rem;
    color: #aaa;
    margin-bottom: 15px;
    border-top: 1px solid rgba(255, 107, 107, 0.2);
    padding-top: 15px;
}

.quiz-info {
    display: flex;
    align-items: center;
    gap: 15px;
}

.quiz-info span {
    display: flex;
    align-items: center;
    gap: 5px;
}

.quiz-info i {
    color: var(--destaque);
}

.read-more {
    display: inline-block;
    background: linear-gradient(to right, var(--destaque), #ff8a8a);
    color: white;
    padding: 8px 20px;
    border-radius: 30px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    width: 100%;
    text-align: center;
}

.read-more:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 107, 107, 0.4);
}

.search-filter {
    display: flex;
    justify-content: space-between;
    margin-bottom: 30px;
    gap: 20px;
}

.search-box {
    flex-grow: 1;
    position: relative;
}

.search-box input {
    width: 100%;
    padding: 12px 20px;
    border-radius: 30px;
    border: 1px solid var(--destaque);
    background: rgba(0, 0, 0, 0.3);
    color: white;
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
}

.search-box input::placeholder {
    color: #aaa;
}

.search-button {
    position: absolute;
    right: 15px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: var(--destaque);
    cursor: pointer;
    font-size: 1rem;
}

.category-select {
    padding: 12px 20px;
    border-radius: 30px;
    border: 1px solid var(--destaque);
    background: rgba(0, 0, 0, 0.3);
    color: white;
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    min-width: 200px;
}

.category-select option {
    background: var(--fundo-card);
    color: white;
}

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    z-index: 1000;
    justify-content: center;
    align-items: center;
}

.modal.active {
    display: flex;
}

.modal-content {
    background: var(--fundo-card);
    border-radius: 12px;
    width: 90%;
    max-width: 800px;
    max-height: 90vh;
    overflow-y: auto;
    padding: 30px;
    position: relative;
    border: 2px solid var(--destaque);
    box-shadow: 0 0 30px rgba(255, 107, 107, 0.3);
}

.close-modal {
    position: absolute;
    top: 15px;
    right: 15px;
    background: none;
    border: none;
    color: var(--destaque);
    font-size: 1.5rem;
    cursor: pointer;
}

.quiz-header {
    text-align: center;
    margin-bottom: 30px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 2px solid var(--destaque);
    padding-bottom: 20px;
}

.quiz-title {
    color: var(--destaque);
    font-size: 1.8rem;
    margin: 0;
}

.timer {
    background: var(--perigo);
    color: white;
    padding: 10px 20px;
    border-radius: 25px;
    font-weight: bold;
    font-size: 1.2rem;
    font-family: 'Orbitron', monospace;
}

.timer.warning {
    background: var(--alerta);
    color: black;
    animation: pulse 1s infinite;
}

.timer.danger {
    background: var(--perigo);
    animation: pulse 0.5s infinite;
}

@keyframes pulse {
    0%, 100% {
        opacity: 1;
        transform: scale(1);
    }
    50% {
        opacity: 0.7;
        transform: scale(1.05);
    }
}

.quiz-progress {
    background: rgba(0, 0, 0, 0.3);
    border-radius: 10px;
    height: 10px;
    margin: 20px 0;
    overflow: hidden;
}

.quiz-progress-bar {
    background: linear-gradient(to right, var(--destaque), #ff8a8a);
    height: 100%;
    transition: width 0.3s ease;
}

.question-container {
    margin-bottom: 30px;
}

.question-number {
    color: var(--destaque);
    font-weight: bold;
    margin-bottom: 10px;
    font-size: 1.1rem;
}

.question-text {
    font-size: 1.3rem;
    margin-bottom: 25px;
    color: white;
    font-weight: 600;
    line-height: 1.4;
}

.answers-container {
    display: grid;
    gap: 15px;
    margin-bottom: 30px;
}

.answer-option {
    background: rgba(0, 0, 0, 0.3);
    border: 2px solid rgba(255, 107, 107, 0.3);
    border-radius: 8px;
    padding: 15px;
    cursor: pointer;
    transition: all 0.3s;
    color: white;
    display: flex;
    align-items: center;
    gap: 15px;
}

.answer-option:hover {
    border-color: var(--destaque);
    background: rgba(255, 107, 107, 0.1);
}

.answer-option.selected {
    border-color: var(--destaque);
    background: rgba(255, 107, 107, 0.2);
}

.answer-option.correct {
    border-color: var(--sucesso);
    background: rgba(0, 230, 118, 0.2);
}

.answer-option.incorrect {
    border-color: var(--perigo);
    background: rgba(255, 61, 0, 0.2);
}

.answer-letter {
    width: 35px;
    height: 35px;
    border-radius: 50%;
    border: 2px solid currentColor;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
    font-size: 1.1rem;
}

.quiz-nav {
    display: flex;
    justify-content: space-between;
    margin-top: 30px;
}

.quiz-button {
    background: linear-gradient(to right, var(--destaque), #ff8a8a);
    color: white;
    border: none;
    padding: 12px 25px;
    border-radius: 30px;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-weight: 600;
    transition: all 0.3s;
    font-size: 1rem;
    display: flex;
    align-items: center;
    gap: 8px;
}

.quiz-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 107, 107, 0.4);
}

.quiz-button:disabled {
    background: #666;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.quiz-button.secondary {
    background: #666;
}

.quiz-button.secondary:hover {
    background: #777;
}

.result-container {
    text-align: center;
}

.result-title {
    color: var(--destaque);
    font-size: 2rem;
    margin-bottom: 20px;
}

.result-score {
    font-size: 4rem;
    font-weight: 700;
    margin: 20px 0;
    background: linear-gradient(to right, var(--destaque), #ff8a8a);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    font-family: 'Orbitron', monospace;
}

.result-message {
    font-size: 1.2rem;
    margin-bottom: 30px;
    color: #ddd;
}

.results-review {
    text-align: left;
    margin-top: 30px;
    max-height: 400px;
    overflow-y: auto;
}

.result-item {
    background: var(--fundo);
    border-radius: 10px;
    padding: 20px;
    margin-bottom: 15px;
    border-left: 4px solid var(--destaque);
}

.result-item.correct {
    border-left-color: var(--sucesso);
}

.result-item.incorrect {
    border-left-color: var(--perigo);
}

.result-question {
    font-weight: bold;
    margin-bottom: 10px;
    color: white;
}

.result-answers {
    margin-bottom: 10px;
}

.result-user-answer {
    color: var(--destaque);
    margin-bottom: 5px;
}

.result-correct-answer {
    color: var(--sucesso);
    margin-bottom: 10px;
}

.result-explanation {
    background: rgba(0, 247, 255, 0.1);
    border: 1px solid var(--azul);
    border-radius: 8px;
    padding: 10px;
    color: var(--azul);
    font-size: 0.9rem;
    line-height: 1.4;
}

.loading {
    text-align: center;
    padding: 50px;
    color: var(--destaque);
    font-size: 1.2rem;
}

.no-results {
    text-align: center;
    padding: 50px;
    color: #aaa;
}

.no-results i {
    font-size: 3rem;
    color: var(--destaque);
    margin-bottom: 20px;
}

.session-timer {
    position: fixed;
    top: 20px;
    right: 20px;
    background: var(--fundo-card);
    border: 2px solid var(--destaque);
    border-radius: 25px;
    padding: 10px 20px;
    z-index: 1001;
    display: flex;
    align-items: center;
    gap: 10px;
    font-family: 'Orbitron', monospace;
    font-weight: bold;
    font-size: 1.1rem;
    color: var(--destaque);
}

@media (max-width: 768px) {
    header {
        flex-direction: column;
        text-align: center;
        padding: 15px;
    }

    .logo h1 {
        font-size: 2rem;
    }

    nav {
        margin-top: 15px;
    }

    nav a {
        margin: 0 10px;
        font-size: 0.9rem;
    }

    .search-filter {
        flex-direction: column;
    }

    .category-select {
        width: 100%;
    }

    .articles-grid {
        grid-template-columns: 1fr;
    }

    .quiz-header {
        flex-direction: column;
        gap: 15px;
        text-align: center;
    }

    .quiz-nav {
        flex-direction: column;
        gap: 10px;
    }

    .quiz-button {
        width: 100%;
        justify-content: center;
    }

    .modal-content {
        padding: 20px;
        margin: 10px;
    }

    .session-timer {
        top: 10px;
        right: 10px;
        padding: 8px 15px;
        font-size: 1rem;
    }
}

/* Scrollbar customization */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: var(--fundo);
}

::-webkit-scrollbar-thumb {
    background: var(--destaque);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: #ff8a8a;
}

