    .category-select {
        width: 100%;
    }

    .modal-content {
        width: 95%;
        padding: 25px;
    }

    .page-title h2 {
        font-size: 2rem;
    }
    
    .modal-title {
        font-size: 1.5rem;
    }
}

@media (max-width: 480px) {
    header {
        flex-direction: column;
        gap: 15px;
        text-align: center;
    }
    
    nav {
        display: flex;
        justify-content: center;
        gap: 10px;
    }
    
    nav a {
        margin: 0;
        padding: 6px 12px;
        font-size: 0.85rem;
    }
    
    .main-container {
        padding: 20px 3%;
    }
}

/* Animations */
@keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
}

.pulse {
    animation: pulse 0.5s ease;
}

@keyframes fadeIn {
    from { 
        opacity: 0; 
        transform: translateY(30px); 
    }
    to { 
        opacity: 1; 
        transform: translateY(0); 
    }
}

.fade-in {
    animation: fadeIn 0.6s ease;
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.slide-in-up {
