// Sistema de notificações
function showNotification(message, type = 'info') {
    const notification = document.createElement('div');
    notification.style.cssText = `
        position: fixed;
        top: 2rem;
        right: 2rem;
        padding: 1rem 1.5rem;
        border-radius: 10px;
        color: white;
        font-weight: bold;
        z-index: 10000;
        transform: translateX(100%);
        transition: all 0.3s ease;
        font-family: 'Rajdhani', sans-serif;
        box-shadow: 0 4px 20px rgba(0,0,0,0.3);
    `;

    const colors = {
        success: 'linear-gradient(45deg, #00ffaa, #00cc88)',
        error: 'linear-gradient(45deg, #ff6b6b, #ff5252)',
        info: 'linear-gradient(45deg, #00f7ff, #0099cc)'
    };

    notification.style.background = colors[type] || colors.info;
    notification.textContent = message;

    document.body.appendChild(notification);

    setTimeout(() => {
        notification.style.transform = 'translateX(0)';
    }, 100);

    setTimeout(() => {
        notification.style.transform = 'translateX(100%)';
        setTimeout(() => {
            if (document.body.contains(notification)) {
                document.body.removeChild(notification);
            }
        }, 300);
    }, 3000);
}

// Funções auxiliares para modal
function likeContent() {
    console.log('Conteúdo curtido!');
    // Implementar lógica de curtir
}

function shareContent() {
    if (navigator.share) {
        navigator.share({
            title: 'FATOMANIA',
            text: 'Confira este conteúdo incrível!',
            url: window.location.href
        });
    } else {
        navigator.clipboard.writeText(window.location.href);
        showNotification('Link copiado para a área de transferência!', 'success');
    }
}

// Função para abrir modal de artigo
function openArticleModal(article) {
    if (!article) {
        console.error('Artigo não fornecido para openArticleModal');
        return;
    }

    // Usa showDetailModal como base
    showDetailModal(article);
}


