        // Dados dos elementos da tabela periódica
        const periodicElements = [
            { number: 1, symbol: 'H', name: 'Hidrogênio', category: 'nonmetal', row: 1, col: 1 },
            { number: 2, symbol: 'He', name: 'Hélio', category: 'noble-gas', row: 1, col: 18 },
