                flex-direction: column;
            }
            
            .featured-image {
                min-height: 200px;
            }
        }

        @media (max-width: 900px) {
            .cards-grid {
                grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            }
            
            .header-actions {
                flex-direction: column-reverse;
                gap: 15px;
                align-items: flex-end;
            }
            
            .search-bar {
                width: 100%;
            }
        }

        @media (max-width: 768px) {
            .dashboard-header {
                flex-direction: column;
                gap: 20px;
                text-align: center;
            }
            
