            --texto-secundario: rgba(255, 255, 255, 0.8);
            --borda: rgba(255, 255, 255, 0.1);
            --erro: #ff4757;
            --sucesso: var(--verde);
            --misterio: #8e44ad;
            --paranormal: #e74c3c;
