        }

        .article-meta {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 0.85rem;
            color: #aaa;
            margin-bottom: 15px;
            border-top: 1px solid #00f7ff(255, 215, 0, 0.2);
            padding-top: 15px;
        }

        .article-date {
            display: flex;
            align-items: center;
        }

        .article-date i {
            margin-right: 5px;
            color: var(--destaque);
        }

        .article-actions {
            display: flex;
            gap: 15px;
        }

        .action-btn {
            background: none;
            border: none;
            color: #aaa;
            cursor: pointer;
            transition: color 0.3s;
            font-size: 0.9rem;
            display: flex;
            align-items: center;
        }

        .action-btn i {
            margin-right: 5px;
        }

        .action-btn:hover {
