        }

        .search-box {
            flex-grow: 1;
        }

        .search-box input {
            width: 100%;
            padding: 12px 20px;
            border-radius: 30px;
            border: 1px solid var(--destaque);
            background: rgba(0, 0, 0, 0.3);
            color: white;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
        }

        .search-box input::placeholder {
            color: #aaa;
        }

        .category-select {
            padding: 12px 20px;
            border-radius: 30px;
            border: 1px solid var(--destaque);
            background: rgba(0, 0, 0, 0.3);
            color: white;
            font-family: 'Rajdhani', sans-serif;
            font-size: 1rem;
            min-width: 200px;
        }

        .category-select option {
            background: var(--fundo-card);
            color: white;
        }

        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.8);
            z-index: 1000;
            justify-content: center;
            align-items: center;
        }

        .modal-content {
            background: var(--fundo-card);
            border-radius: 12px;
            width: 90%;
            max-width: 800px;
            max-height: 90vh;
            overflow-y: auto;
            padding: 30px;
            position: relative;
            border: 2px solid var(--destaque);
            box-shadow: 0 0 30px rgba(255, 215, 0, 0.3);
        }

        .close-modal {
            position: absolute;
            top: 15px;
            right: 15px;
            background: none;
            border: none;
            color: var(--destaque);
            font-size: 1.5rem;
            cursor: pointer;
        }

        .modal-title {
            color: var(--destaque);
            font-size: 1.8rem;
            margin-bottom: 10px;
        }

        .modal-author {
            color: var(--azul);
            font-size: 1.1rem;
            margin-bottom: 20px;
            display: block;
        }

        .modal-date {
            color: #aaa;
            margin-bottom: 20px;
            display: block;
        }

        .modal-image {
            width: 100%;
            height: 300px;
            background-size: cover;
            background-position: center;
            margin-bottom: 20px;
            border-radius: 8px;
        }

        .modal-text {
            line-height: 1.8;
            margin-bottom: 30px;
            color: #ddd;
        }

        .comment-section {
            margin-top: 40px;
            border-top: 1px solid rgba(255, 215, 0, 0.3);
            padding-top: 20px;
        }

        .comment-title {
            color: var(--destaque);
            font-size: 1.3rem;
            margin-bottom: 20px;
        }

        .comment-form {
            margin-bottom: 30px;
        }

        .comment-input {
            width: 100%;
            padding: 15px;
            border-radius: 8px;
            border: 1px solid var(--destaque);
            background: rgba(0, 0, 0, 0.3);
            color: white;
            font-family: 'Rajdhani', sans-serif;
            margin-bottom: 10px;
            min-height: 100px;
            resize: vertical;
        }

        .comment-input::placeholder {
            color: #aaa;
        }

        .comment-submit {
            background: linear-gradient(to right, var(--destaque), #ffaa00);
            color: #222;
            padding: 10px 25px;
            border-radius: 30px;
            border: none;
            cursor: pointer;
            font-family: 'Rajdhani', sans-serif;
            font-weight: 600;
            transition: all 0.3s;
        }

        .comment-submit:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px #00f7ff(255, 215, 0, 0.4);
        }

        .comments-list {
            margin-top: 20px;
        }

        .comment {
            background: rgba(0, 0, 0, 0.2);
            padding: 15px;
            border-radius: 8px;
            margin-bottom: 15px;
            border-left: 3px solid var(--destaque);
        }

        .comment-author {
            font-weight: 600;
            color: var(--destaque);
            margin-bottom: 5px;
        }

        .comment-date {
            font-size: 0.8rem;
            color: #aaa;
            margin-bottom: 10px;
        }

        .comment-text {
            line-height: 1.5;
        }

        .loading {
            text-align: center;
            padding: 40px;
            color: #aaa;
        }

        .no-results {
            text-align: center;
            padding: 40px;
            color: #aaa;
            font-size: 1.1rem;
        }

        @media (max-width: 768px) {
            .articles-grid {
                grid-template-columns: 1fr;
            }
            
            .search-filter {
                flex-direction: column;
            }
            
            .category-select {
                width: 100%;
            }

            .modal-content {
                width: 95%;
                padding: 20px;
            }

            .page-title h2 {
                font-size: 1.8rem;
            }
        }

        /* AnimaÃ§Ãµes */
        @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.1); }
            100% { transform: scale(1); }
        }

        .pulse {
            animation: pulse 0.5s ease;
        }

        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }

        .fade-in {
            animation: fadeIn 0.5s ease;
        }

css/spacemania.css.content:
:root {
    --primary-purple: #7209b7;
    --secondary-purple: #9d4edd;
    --dark-purple: #5a189a;
    --background: #0d1117;
    --card-background: #161b22;
    --accent: #7209b7;
    --danger: #ef4444;
    --success: #10b981;
    --warning: #f59e0b;
    --text-light: #f0f6fc;
    --text-muted: #8b949e;
    --space-blue: #240046;
}

body {
    margin: 0;
    padding: 0;
    background: var(--background);
    font-family: 'Rajdhani', sans-serif;
    color: var(--text-light);
    line-height: 1.6;
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(114, 9, 183, 0.15) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(157, 78, 221, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(36, 0, 70, 0.1) 0%, transparent 50%);
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 5%;
    background: linear-gradient(135deg, rgba(13, 17, 23, 0.95), rgba(114, 9, 183, 0.1));
    position: sticky;
    top: 0;
    z-index: 100;
    backdrop-filter: blur(10px);
    border-bottom: 2px solid var(--primary-purple);
    box-shadow: 0 4px 20px rgba(114, 9, 183, 0.3);
}

.logo h1 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    background: linear-gradient(90deg, var(--primary-purple), var(--secondary-purple));
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 20px rgba(114, 9, 183, 0.6);
    letter-spacing: 2px;
}

.slogan {
    margin: 5px 0 0;
    font-size: 0.9rem;
    color: var(--secondary-purple);
    font-style: italic;
    max-width: 450px;
    line-height: 1.4;
}

nav a {
    color: var(--text-light);
    text-decoration: none;
    margin-left: 25px;
    font-weight: 600;
    transition: all 0.3s ease;
    padding: 8px 16px;
    border-radius: 20px;
    position: relative;
}

nav a:hover {
    color: var(--primary-purple);
    background: rgba(114, 9, 183, 0.15);
    transform: translateY(-2px);
}

nav a i {
    margin-right: 8px;
}

.main-container {
    padding: 40px 5%;
    max-width: 1400px;
    margin: 0 auto;
}

.page-title {
    text-align: center;
    margin-bottom: 50px;
    position: relative;
}

.page-title h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    color: var(--primary-purple);
    display: inline-block;
    position: relative;
}

.page-title h2::after {
    content: '';
    display: block;
    width: 120px;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-purple), var(--secondary-purple));
    margin: 15px auto 0;
    border-radius: 2px;
    box-shadow: 0 0 15px rgba(114, 9, 183, 0.6);
}

.search-filter {
    display: flex;
    justify-content: space-between;
    margin-bottom: 40px;
    gap: 20px;
    align-items: center;
}

.search-box {
    flex-grow: 1;
    position: relative;
}

.search-box input {
    width: 100%;
    padding: 15px 25px;
    border-radius: 30px;
    border: 2px solid var(--primary-purple);
    background: rgba(22, 27, 34, 0.8);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(114, 9, 183, 0.2);
}

.search-box input:focus {
    outline: none;
    border-color: var(--secondary-purple);
    box-shadow: 0 0 25px rgba(114, 9, 183, 0.4);
    background: rgba(22, 27, 34, 0.9);
}

.search-box input::placeholder {
    color: var(--text-muted);
}

.category-select {
    padding: 15px 25px;
    border-radius: 30px;
    border: 2px solid var(--primary-purple);
    background: rgba(22, 27, 34, 0.8);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1.1rem;
    min-width: 220px;
    cursor: pointer;
    transition: all 0.3s ease;
}

.category-select:focus {
    outline: none;
    border-color: var(--secondary-purple);
    box-shadow: 0 0 25px rgba(114, 9, 183, 0.4);
}

.category-select option {
    background: var(--card-background);
    color: var(--text-light);
    padding: 10px;
}

.articles-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
    gap: 30px;
    margin-bottom: 40px;
}

.article-card {
    background: var(--card-background);
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(114, 9, 183, 0.3);
    transition: all 0.3s ease;
    position: relative;
    backdrop-filter: blur(10px);
}

.article-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 15px 35px rgba(114, 9, 183, 0.3);
    border-color: var(--primary-purple);
}

.article-image {
    height: 200px;
    background-size: cover;
    background-position: center;
    position: relative;
    overflow: hidden;
}

.article-image::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        45deg,
        rgba(114, 9, 183, 0.2) 0%,
        transparent 50%,
        rgba(157, 78, 221, 0.15) 100%
    );
    z-index: 1;
}

.article-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, transparent 50%, rgba(0, 0, 0, 0.8));
    z-index: 2;
}

.article-category {
    position: absolute;
    top: 15px;
    right: 15px;
    background: linear-gradient(135deg, var(--primary-purple), var(--secondary-purple));
    color: white;
    padding: 8px 16px;
    border-radius: 25px;
    font-size: 0.85rem;
    font-weight: 600;
    z-index: 3;
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 4px 15px rgba(114, 9, 183, 0.4);
}

.article-content {
    padding: 25px;
}

.article-title {
    font-size: 1.4rem;
    margin: 0 0 12px;
    color: var(--primary-purple);
    font-weight: 700;
    line-height: 1.3;
    font-family: 'Orbitron', sans-serif;
}

.article-author {
    font-size: 1rem;
    color: var(--secondary-purple);
    margin-bottom: 15px;
    display: block;
    font-weight: 600;
}

.article-excerpt {
    font-size: 1rem;
    margin-bottom: 20px;
    color: var(--text-muted);
    line-height: 1.6;
}

.article-meta {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 20px;
    border-top: 1px solid rgba(114, 9, 183, 0.3);
    padding-top: 15px;
}

.article-date {
    display: flex;
    align-items: center;
}

.article-date i {
    margin-right: 8px;
    color: var(--primary-purple);
}

.article-actions {
    display: flex;
    gap: 15px;
}

.action-btn {
    background: none;
    border: none;
    color: var(--text-muted);
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;
    display: flex;
    align-items: center;
    padding: 5px 10px;
    border-radius: 15px;
}

.action-btn i {
    margin-right: 5px;
}

.action-btn:hover {
    color: var(--primary-purple);
    background: rgba(114, 9, 183, 0.15);
    transform: scale(1.1);
}

.action-btn.liked {
    color: var(--danger);
}

.action-btn.favorited {
    color: var(--primary-purple);
}

.read-more {
    display: inline-block;
    background: linear-gradient(135deg, var(--primary-purple), var(--secondary-purple));
    color: white;
    padding: 12px 25px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s ease;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 4px 15px rgba(114, 9, 183, 0.4);
}

.read-more:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(114, 9, 183, 0.5);
    background: linear-gradient(135deg, var(--secondary-purple), var(--dark-purple));
}

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    z-index: 1000;
    justify-content: center;
    align-items: center;
    backdrop-filter: blur(5px);
}

.modal-content {
    background: var(--card-background);
    border-radius: 20px;
    width: 90%;
    max-width: 900px;
    max-height: 90vh;
    overflow-y: auto;
    padding: 40px;
    position: relative;
    border: 2px solid var(--primary-purple);
    box-shadow: 0 0 50px rgba(114, 9, 183, 0.4);
}

.close-modal {
    position: absolute;
    top: 20px;
    right: 20px;
    background: none;
    border: none;
    color: var(--primary-purple);
    font-size: 2rem;
    cursor: pointer;
    transition: all 0.3s ease;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.close-modal:hover {
    background: rgba(114, 9, 183, 0.15);
    transform: scale(1.1);
}

.modal-title {
    color: var(--primary-purple);
    font-size: 2rem;
    margin-bottom: 15px;
    font-family: 'Orbitron', sans-serif;
    line-height: 1.3;
}

.modal-author {
    color: var(--secondary-purple);
    font-size: 1.2rem;
    margin-bottom: 10px;
    display: block;
    font-weight: 600;
}

.modal-date {
    color: var(--text-muted);
    margin-bottom: 25px;
    display: block;
    font-size: 1rem;
}

.modal-image {
    width: 100%;
    height: 300px;
    background-size: cover;
    background-position: center;
    margin-bottom: 30px;
    border-radius: 15px;
    border: 2px solid rgba(114, 9, 183, 0.3);
}

.modal-text {
    line-height: 1.8;
    margin-bottom: 40px;
    color: var(--text-light);
    font-size: 1.1rem;
}

.modal-text p {
    margin-bottom: 20px;
}

.comment-section {
    margin-top: 50px;
    border-top: 2px solid rgba(114, 9, 183, 0.3);
    padding-top: 30px;
}

.comment-title {
    color: var(--primary-purple);
    font-size: 1.5rem;
    margin-bottom: 25px;
    font-family: 'Orbitron', sans-serif;
}

.comment-form {
    margin-bottom: 35px;
}

.comment-input {
    width: 100%;
    padding: 20px;
    border-radius: 15px;
    border: 2px solid var(--primary-purple);
    background: rgba(13, 17, 23, 0.5);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    margin-bottom: 15px;
    min-height: 120px;
    resize: vertical;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.comment-input:focus {
    outline: none;
    border-color: var(--secondary-purple);
    box-shadow: 0 0 25px rgba(114, 9, 183, 0.4);
}

.comment-input::placeholder {
    color: var(--text-muted);
}

.comment-submit {
    background: linear-gradient(135deg, var(--primary-purple), var(--secondary-purple));
    color: white;
    padding: 12px 30px;
    border-radius: 25px;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-weight: 600;
    transition: all 0.3s ease;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.comment-submit:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(114, 9, 183, 0.5);
}

.comments-list {
    margin-top: 25px;
}

.comment {
    background: rgba(13, 17, 23, 0.3);
    padding: 20px;
    border-radius: 15px;
    margin-bottom: 20px;
    border-left: 4px solid var(--primary-purple);
    transition: all 0.3s ease;
}

.comment:hover {
    background: rgba(13, 17, 23, 0.5);
    transform: translateX(5px);
}

.comment-author {
    font-weight: 600;
    color: var(--primary-purple);
    margin-bottom: 8px;
    font-size: 1.1rem;
}

.comment-date {
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 12px;
}

.comment-text {
    line-height: 1.6;
    color: var(--text-light);
}

.loading {
    text-align: center;
    padding: 60px;
    color: var(--text-muted);
    font-size: 1.2rem;
}

.loading i {
    color: var(--primary-purple);
    margin-right: 10px;
}

.no-results {
    text-align: center;
    padding: 60px;
    color: var(--text-muted);
    font-size: 1.2rem;
}

.no-results i {
    color: var(--primary-purple);
    font-size: 3rem;
    margin-bottom: 20px;
    display: block;
}

.no-results h3 {
    color: var(--primary-purple);
    margin-bottom: 10px;
}

/* Responsive Design */
@media (max-width: 768px) {
    .logo h1 {
        font-size: 2rem;
    }
    
    .slogan {
        font-size: 0.8rem;
    }
    
    nav a {
        margin-left: 15px;
        font-size: 0.9rem;
    }
    
    .articles-grid {
        grid-template-columns: 1fr;
        gap: 20px;
    }
    
    .search-filter {
        flex-direction: column;
        gap: 15px;
    }
    
    .category-select {
        width: 100%;
    }

    .modal-content {
        width: 95%;
        padding: 25px;
    }

    .page-title h2 {
        font-size: 2rem;
    }
    
    .modal-title {
        font-size: 1.5rem;
    }
}

@media (max-width: 480px) {
    header {
        flex-direction: column;
        gap: 15px;
        text-align: center;
    }
    
    nav {
        display: flex;
        justify-content: center;
        gap: 10px;
    }
    
    nav a {
        margin: 0;
        padding: 6px 12px;
        font-size: 0.85rem;
    }
    
    .main-container {
        padding: 20px 3%;
    }
}

/* Animations */
@keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
}

.pulse {
    animation: pulse 0.5s ease;
}

@keyframes fadeIn {
    from { 
        opacity: 0; 
        transform: translateY(30px); 
    }
    to { 
        opacity: 1; 
        transform: translateY(0); 
    }
}

.fade-in {
    animation: fadeIn 0.6s ease;
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.slide-in-up {
    animation: slideInUp 0.5s ease;
}

/* Notification styles */
.notification {
    position: fixed;
    top: 20px;
    right: 20px;
    background: linear-gradient(135deg, var(--primary-purple), var(--secondary-purple));
    color: white;
    padding: 15px 25px;
    border-radius: 25px;
    z-index: 10000;
    box-shadow: 0 8px 25px rgba(114, 9, 183, 0.4);
    transform: translateX(400px);
    transition: transform 0.3s ease;
}

.notification.show {
    transform: translateX(0);
}

/* Space-themed effects */
@keyframes twinkle {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 1; }
}

.article-card::before {
    content: '';
    position: absolute;
    top: 10px;
    left: 10px;
    width: 4px;
    height: 4px;
    background: var(--primary-purple);
    border-radius: 50%;
    animation: twinkle 2s infinite;
    z-index: 1;
}

.article-card:nth-child(2n)::before {
    animation-delay: 0.5s;
}

.article-card:nth-child(3n)::before {
    animation-delay: 1s;
}

css/sportmania.css.content:
:root {
    --primary-red: #e63946;
    --secondary-red: #f1faee;
    --dark-red: #d62828;
    --light-red: #f8d7da;
    --accent-blue: #457b9d;
    --background: #1d3557;
    --card-background: #2e4057;
    --danger: #dc3545;
    --success: #28a745;
    --warning: #ffc107;
    --text-light: #f1faee;
    --text-muted: #a8dadc;
    --sport-orange: #f77f00;
}

body {
    margin: 0;
    padding: 0;
    background: var(--background);
    font-family: 'Rajdhani', sans-serif;
    color: var(--text-light);
    line-height: 1.6;
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(230, 57, 70, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(69, 123, 157, 0.08) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(247, 127, 0, 0.05) 0%, transparent 50%);
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 5%;
    background: linear-gradient(135deg, rgba(29, 53, 87, 0.95), rgba(230, 57, 70, 0.1));
    position: sticky;
    top: 0;
    z-index: 100;
    backdrop-filter: blur(10px);
    border-bottom: 2px solid var(--primary-red);
    box-shadow: 0 4px 20px rgba(230, 57, 70, 0.3);
}

.logo h1 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    background: linear-gradient(90deg, var(--primary-red), var(--sport-orange));
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 20px rgba(230, 57, 70, 0.6);
    letter-spacing: 2px;
}

.slogan {
    margin: 5px 0 0;
    font-size: 0.9rem;
    color: var(--accent-blue);
    font-style: italic;
    max-width: 450px;
    line-height: 1.4;
}

nav a {
    color: var(--text-light);
    text-decoration: none;
    margin-left: 20px;
    font-weight: 600;
    transition: all 0.3s ease;
    padding: 8px 16px;
    border-radius: 20px;
    position: relative;
}

nav a:hover {
    color: var(--primary-red);
    background: rgba(230, 57, 70, 0.15);
    transform: translateY(-2px);
}

nav a i {
    margin-right: 8px;
}

.main-container {
    padding: 40px 5%;
    max-width: 1400px;
    margin: 0 auto;
}

.page-title {
    text-align: center;
    margin-bottom: 50px;
    position: relative;
}

.page-title h2 {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin: 0;
    color: var(--primary-red);
    display: inline-block;
    position: relative;
}

.page-title h2::after {
    content: '';
    display: block;
    width: 120px;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-red), var(--sport-orange));
    margin: 15px auto 0;
    border-radius: 2px;
    box-shadow: 0 0 15px rgba(230, 57, 70, 0.6);
}

.search-filter {
    display: flex;
    justify-content: space-between;
    margin-bottom: 40px;
    gap: 20px;
    align-items: center;
}

.search-box {
    flex-grow: 1;
    position: relative;
}

.search-box input {
    width: 100%;
    padding: 15px 25px;
    border-radius: 30px;
    border: 2px solid var(--primary-red);
    background: rgba(46, 64, 87, 0.8);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(230, 57, 70, 0.2);
}

.search-box input:focus {
    outline: none;
    border-color: var(--sport-orange);
    box-shadow: 0 0 25px rgba(230, 57, 70, 0.4);
    background: rgba(46, 64, 87, 0.9);
}

.search-box input::placeholder {
    color: var(--text-muted);
}

.category-select {
    padding: 15px 25px;
    border-radius: 30px;
    border: 2px solid var(--primary-red);
    background: rgba(46, 64, 87, 0.8);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    font-size: 1.1rem;
    min-width: 220px;
    cursor: pointer;
    transition: all 0.3s ease;
}

.category-select:focus {
    outline: none;
    border-color: var(--sport-orange);
    box-shadow: 0 0 25px rgba(230, 57, 70, 0.4);
}

.category-select option {
    background: var(--card-background);
    color: var(--text-light);
    padding: 10px;
}

.articles-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
    gap: 30px;
    margin-bottom: 40px;
}

.article-card {
    background: var(--card-background);
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(230, 57, 70, 0.3);
    transition: all 0.3s ease;
    position: relative;
    backdrop-filter: blur(10px);
}

.article-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 15px 35px rgba(230, 57, 70, 0.3);
    border-color: var(--primary-red);
}

.article-image {
    height: 200px;
    background-size: cover;
    background-position: center;
    position: relative;
    overflow: hidden;
}

.article-image::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        45deg,
        rgba(230, 57, 70, 0.2) 0%,
        transparent 50%,
        rgba(247, 127, 0, 0.15) 100%
    );
    z-index: 1;
}

.article-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, transparent 50%, rgba(0, 0, 0, 0.8));
    z-index: 2;
}

.article-category {
    position: absolute;
    top: 15px;
    right: 15px;
    background: linear-gradient(135deg, var(--primary-red), var(--sport-orange));
    color: white;
    padding: 8px 16px;
    border-radius: 25px;
    font-size: 0.85rem;
    font-weight: 600;
    z-index: 3;
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 4px 15px rgba(230, 57, 70, 0.4);
}

.article-content {
    padding: 25px;
}

.article-title {
    font-size: 1.4rem;
    margin: 0 0 12px;
    color: var(--primary-red);
    font-weight: 700;
    line-height: 1.3;
    font-family: 'Orbitron', sans-serif;
}

.article-author {
    font-size: 1rem;
    color: var(--accent-blue);
    margin-bottom: 15px;
    display: block;
    font-weight: 600;
}

.article-excerpt {
    font-size: 1rem;
    margin-bottom: 20px;
    color: var(--text-muted);
    line-height: 1.6;
}

.article-meta {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 20px;
    border-top: 1px solid rgba(230, 57, 70, 0.3);
    padding-top: 15px;
}

.article-date {
    display: flex;
    align-items: center;
}

.article-date i {
    margin-right: 8px;
    color: var(--primary-red);
}

.article-actions {
    display: flex;
    gap: 15px;
}

.action-btn {
    background: none;
    border: none;
    color: var(--text-muted);
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;
    display: flex;
    align-items: center;
    padding: 5px 10px;
    border-radius: 15px;
}

.action-btn i {
    margin-right: 5px;
}

.action-btn:hover {
    color: var(--primary-red);
    background: rgba(230, 57, 70, 0.15);
    transform: scale(1.1);
}

.action-btn.liked {
    color: var(--danger);
}

.action-btn.favorited {
    color: var(--primary-red);
}

.read-more {
    display: inline-block;
    background: linear-gradient(135deg, var(--primary-red), var(--sport-orange));
    color: white;
    padding: 12px 25px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s ease;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 4px 15px rgba(230, 57, 70, 0.4);
}

.read-more:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(230, 57, 70, 0.5);
    background: linear-gradient(135deg, var(--dark-red), var(--primary-red));
}

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    z-index: 1000;
    justify-content: center;
    align-items: center;
    backdrop-filter: blur(5px);
}

.modal-content {
    background: var(--card-background);
    border-radius: 20px;
    width: 90%;
    max-width: 900px;
    max-height: 90vh;
    overflow-y: auto;
    padding: 40px;
    position: relative;
    border: 2px solid var(--primary-red);
    box-shadow: 0 0 50px rgba(230, 57, 70, 0.4);
}

.close-modal {
    position: absolute;
    top: 20px;
    right: 20px;
    background: none;
    border: none;
    color: var(--primary-red);
    font-size: 2rem;
    cursor: pointer;
    transition: all 0.3s ease;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.close-modal:hover {
    background: rgba(230, 57, 70, 0.15);
    transform: scale(1.1);
}

.modal-title {
    color: var(--primary-red);
    font-size: 2rem;
    margin-bottom: 15px;
    font-family: 'Orbitron', sans-serif;
    line-height: 1.3;
}

.modal-author {
    color: var(--accent-blue);
    font-size: 1.2rem;
    margin-bottom: 10px;
    display: block;
    font-weight: 600;
}

.modal-date {
    color: var(--text-muted);
    margin-bottom: 25px;
    display: block;
    font-size: 1rem;
}

.modal-image {
    width: 100%;
    height: 300px;
    background-size: cover;
    background-position: center;
    margin-bottom: 30px;
    border-radius: 15px;
    border: 2px solid rgba(230, 57, 70, 0.3);
}

.modal-text {
    line-height: 1.8;
    margin-bottom: 40px;
    color: var(--text-light);
    font-size: 1.1rem;
}

.modal-text p {
    margin-bottom: 20px;
}

.comment-section {
    margin-top: 50px;
    border-top: 2px solid rgba(230, 57, 70, 0.3);
    padding-top: 30px;
}

.comment-title {
    color: var(--primary-red);
    font-size: 1.5rem;
    margin-bottom: 25px;
    font-family: 'Orbitron', sans-serif;
}

.comment-form {
    margin-bottom: 35px;
}

.comment-input {
    width: 100%;
    padding: 20px;
    border-radius: 15px;
    border: 2px solid var(--primary-red);
    background: rgba(29, 53, 87, 0.5);
    color: var(--text-light);
    font-family: 'Rajdhani', sans-serif;
    margin-bottom: 15px;
    min-height: 120px;
    resize: vertical;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.comment-input:focus {
    outline: none;
    border-color: var(--sport-orange);
    box-shadow: 0 0 25px rgba(230, 57, 70, 0.4);
}

.comment-input::placeholder {
    color: var(--text-muted);
}

.comment-submit {
    background: linear-gradient(135deg, var(--primary-red), var(--sport-orange));
    color: white;
    padding: 12px 30px;
    border-radius: 25px;
    border: none;
    cursor: pointer;
    font-family: 'Rajdhani', sans-serif;
    font-weight: 600;
    transition: all 0.3s ease;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.comment-submit:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(230, 57, 70, 0.5);
}

.comments-list {
    margin-top: 25px;
}

.comment {
    background: rgba(29, 53, 87, 0.3);
    padding: 20px;
    border-radius: 15px;
    margin-bottom: 20px;
    border-left: 4px solid var(--primary-red);
    transition: all 0.3s ease;
}

.comment:hover {
    background: rgba(29, 53, 87, 0.5);
    transform: translateX(5px);
}

.comment-author {
    font-weight: 600;
    color: var(--primary-red);
    margin-bottom: 8px;
    font-size: 1.1rem;
}

.comment-date {
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 12px;
}

.comment-text {
    line-height: 1.6;
    color: var(--text-light);
}

.loading {
    text-align: center;
    padding: 60px;
    color: var(--text-muted);
    font-size: 1.2rem;
}

.loading i {
    color: var(--primary-red);
    margin-right: 10px;
}

.no-results {
    text-align: center;
    padding: 60px;
    color: var(--text-muted);
    font-size: 1.2rem;
}

.no-results i {
    color: var(--primary-red);
    font-size: 3rem;
    margin-bottom: 20px;
    display: block;
}

.no-results h3 {
    color: var(--primary-red);
