// FunÃ§Ã£o para aguardar autenticaÃ§Ã£o com timeout melhorado
async function waitForAuth(timeout = 10000) {
    return new Promise((resolve) => {
        let resolved = false;

        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (!resolved) {
                resolved = true;
                unsubscribe();
                resolve(user);
            }
        });

        setTimeout(() => {
            if (!resolved) {
                resolved = true;
                unsubscribe();
                resolve(null);
            }
        }, timeout);
    });
}

// Verifica autenticaÃ§Ã£o e conectividade
async function checkAuthState() {
    console.log('ðŸ”¥ Verificando autenticaÃ§Ã£o...');

    // Verifica se Firebase estÃ¡ carregado
    if (!window.firebaseAuth || !window.firebaseDB) {
        console.warn('âš ï¸ Firebase nÃ£o estÃ¡ completamente carregado, aguardando...');
        setTimeout(checkAuthState, 1000);
        return;
    }

    const user = await waitForAuth();
    if (user) {
        currentUser = user;
        await loadUserProfile(user);
        await updateUserUI(user);
        loadContent();
        startAvatarAnimation();
    } else {
        showAuthRequiredMessage();
    }
}


