// FunÃ§Ã£o auxiliar para obter iniciais
function getInitials(profile, user) {
    const name = profile?.name || user?.displayName || user?.email || 'U';
    return name
        .split(' ')
        .map(n => n[0])
        .join('')
        .toUpperCase()
        .slice(0, 2);
}

