// FunÃ§Ã£o para carregar notÃ­cias da base de dados
async function loadNewsFromDatabase() {
    try {
        console.log('ðŸ”„ Carregando notÃ­cias da base de dados...');

        let databaseNews = [];

        try {
            // Tenta primeiro com ordenaÃ§Ã£o
            const newsQuery = query(
                collection(db, "news"),
                orderBy("createdAt", "desc"),
                limit(10)
            );
            const querySnapshot = await getDocs(newsQuery);

            querySnapshot.forEach((doc) => {
                const data = doc.data();
                databaseNews.push({
                    id: doc.id,
                    ...data,
                    likes: data.likes || 0,
                    comments: data.comments || [],
                    likedBy: data.likedBy || []
                });
            });
        } catch (indexError) {
            console.warn('Erro com Ã­ndice composto, tentando consulta simples:', indexError);

            // Se falhar (failed-precondition), tenta consulta simples
            try {
                const simpleQuery = query(collection(db, "news"), limit(10));
                const querySnapshot = await getDocs(simpleQuery);

                querySnapshot.forEach((doc) => {
                    const data = doc.data();
                    databaseNews.push({
                        id: doc.id,
                        ...data,
                        likes: data.likes || 0,
                        comments: data.comments || [],
                        likedBy: data.likedBy || []
                    });
                });

                // Ordena no cliente
                databaseNews.sort((a, b) => {
                    const dateA = a.createdAt?.seconds || a.createdAt?.toDate?.() || new Date(a.createdAt || 0);
                    const dateB = b.createdAt?.seconds || b.createdAt?.toDate?.() || new Date(b.createdAt || 0);
                    return new Date(dateB) - new Date(dateA);
                });

            } catch (simpleError) {
                console.error('Erro mesmo com consulta simples:', simpleError);
                throw simpleError;
            }
        }

        if (databaseNews.length === 0) {
            console.log('ðŸ“ Criando notÃ­cias de exemplo...');
            const sampleNews = await createSampleNews();
            return sampleNews;
        }

        console.log('âœ… NotÃ­cias carregadas:', databaseNews.length);
        return databaseNews;

    } catch (error) {
        console.error('âŒ Erro ao carregar notÃ­cias:', error);
        console.log('ðŸ“ Usando notÃ­cias estÃ¡ticas como fallback...');
        return getStaticHighlights();
    }
}

