    `;

    if (elements.modalContent) {
        elements.modalContent.innerHTML += commentsHTML;
    }
}

// Renderiza lista de comentÃ¡rios
function renderComments(comments) {
    if (comments.length === 0) {
        return '<p style="color: var(--texto-secundario); text-align: center; padding: 1rem;">Ainda nÃ£o hÃ¡ comentÃ¡rios. Seja o primeiro!</p>';
    }

    return comments.map(comment => `
        <div class="comment-item" style="
            background: rgba(255,255,255,0.05);
            border-radius: 10px;
            padding: 1rem;
            margin-bottom: 1rem;
            border-left: 3px solid var(--azul);
        ">
            <div class="comment-header" style="display: flex; align-items: center; gap: 0.5rem; margin-bottom: 0.5rem;">
                <div class="comment-avatar" style="
                    width: 30px;
                    height: 30px;
                    border-radius: 50%;
                    background: linear-gradient(45deg, var(--azul), var(--roxo));
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    font-size: 0.8rem;
                    color: white;
                ">${comment.authorName ? comment.authorName.charAt(0).toUpperCase() : 'U'}</div>
                <strong style="color: var(--azul);">${comment.authorName || 'UsuÃ¡rio'}</strong>
                <span style="color: var(--texto-secundario); font-size: 0.8rem;">
                    ${comment.createdAt ? new Date(comment.createdAt.seconds * 1000).toLocaleDateString('pt-BR') : 'Agora'}
                </span>
            </div>
            <p style="color: var(--texto); line-height: 1.5;">${comment.content}</p>
        </div>
    `).join('');
}

// Adiciona comentÃ¡rio
async function addComment(articleId) {
    const commentInput = document.getElementById('commentInput');
    if (!commentInput || !currentUser || !userProfile) {
        showNotification('FaÃ§a login para comentar', 'error');
        return;
    }

    const content = commentInput.value.trim();
    if (!content) {
        showNotification('Digite um comentÃ¡rio vÃ¡lido', 'error');
        return;
    }

    try {
        const newComment = {
            content: content,
            authorId: currentUser.uid,
            authorName: userProfile.name || currentUser.displayName || 'UsuÃ¡rio',
            createdAt: new Date().toISOString(),
            timestamp: Date.now()
        };

        // Primeiro tenta atualizar na base de dados
        try {
            const articleRef = doc(db, "news", articleId);
            await updateDoc(articleRef, {
                comments: arrayUnion(newComment)
            });
        } catch (dbError) {
            console.warn('Erro na base de dados, salvando localmente:', dbError);
            // Se falhar, salva apenas localmente
        }

        // Atualiza dados locais sempre
        const newsIndex = newsData.findIndex(n => n.id === articleId);
        if (newsIndex !== -1) {
            if (!newsData[newsIndex].comments) newsData[newsIndex].comments = [];
            newsData[newsIndex].comments.push(newComment);
        }

        // Atualiza o contador de comentÃ¡rios no modal
        // Limpa input e atualiza UI
        commentInput.value = '';

        // Re-renderiza comentÃ¡rios
        const commentsList = document.getElementById('commentsList');
        if (commentsList) {
            const article = newsData.find(n => n.id === articleId);
            commentsList.innerHTML = renderComments(article?.comments || []);
        }

        // Atualiza a contagem de comentÃ¡rios
        const commentsSection = document.querySelector('.comments-section h4');
        if (commentsSection && newsData[newsIndex]) {
            commentsSection.innerHTML = `<i class="fas fa-comments"></i> ComentÃ¡rios (${newsData[newsIndex].comments.length})`;
        }

        showNotification('ComentÃ¡rio adicionado!', 'success');

    } catch (error) {
        console.error('Erro ao adicionar comentÃ¡rio:', error);
        showNotification('Erro ao adicionar comentÃ¡rio. Tente novamente.', 'error');
    }
}

// Toggle like functionality CORRIGIDO
async function toggleLike(articleId) {
    if (!currentUser) {
        showNotification('FaÃ§a login para curtir artigos', 'error');
        return;
    }

    // Encontra o artigo nos dados locais
    const newsIndex = newsData.findIndex(n => n.id === articleId);
    if (newsIndex === -1) {
        showNotification('Artigo nÃ£o encontrado', 'error');
        return;
    }

    const article = newsData[newsIndex];
    const likedBy = article.likedBy || [];
    const isLiked = likedBy.includes(currentUser.uid);

    let newLikedBy, newLikes;

    if (isLiked) {
        newLikedBy = likedBy.filter(uid => uid !== currentUser.uid);
        newLikes = Math.max(0, (article.likes || 0) - 1);
    } else {
        newLikedBy = [...likedBy, currentUser.uid];
        newLikes = (article.likes || 0) + 1;
    }

    // Atualiza UI imediatamente
    const likeBtn = document.getElementById('likeBtn');
    const likeCount = document.getElementById('likeCount');

    if (likeCount) likeCount.textContent = newLikes;
    if (likeBtn) likeBtn.style.color = !isLiked ? '#ff6b6b' : 'var(--texto)';

    // Atualiza dados locais
    newsData[newsIndex].likes = newLikes;
    newsData[newsIndex].likedBy = newLikedBy;

    try {
        // Tenta atualizar na base de dados
        const articleRef = doc(db, "news", articleId);
        await updateDoc(articleRef, {
            likes: newLikes,
            likedBy: newLikedBy
        });

        showNotification(isLiked ? 'Curtida removida' : 'Artigo curtido!', 'success');

    } catch (error) {
        console.warn('Erro ao atualizar like na base de dados:', error);
        // MantÃ©m as alteraÃ§Ãµes locais mesmo se a base de dados falhar
        showNotification(isLiked ? 'Curtida removida (offline)' : 'Artigo curtido (offline)!', 'success');
    }
}

// Share article functionality
function shareArticle(article) {
    if (navigator.share) {
        navigator.share({
            title: article.title,
            text: article.description,
            url: window.location.href
        });
    } else {
        const shareText = `${article.title} - ${article.description}\n\n${window.location.href}`;
        navigator.clipboard.writeText(shareText).then(() => {
            showNotification('Link copiado para a Ã¡rea de transferÃªncia!', 'success');
        });
    }
}

function closeArticleModal() {
    if (elements.detailModal) {
        elements.detailModal.style.display = 'none';
