// InicializaÃ§Ã£o das partÃ­culas cientÃ­ficas
document.addEventListener('DOMContentLoaded', () => {
        createScienceParticles();
        console.log('ðŸ”¬ LABMANIA - Sistema de LaboratÃ³rios CientÃ­ficos inicializado');
});

// Criar partÃ­culas cientÃ­ficas animadas
function createScienceParticles() {
        const particlesContainer = document.getElementById('scienceParticles');
        const particleCount = 30;
        
        for (let i = 0; i < particleCount; i++) {
                const particle = document.createElement('div');
                particle.className = 'molecule-particle';
                
                // Propriedades aleatÃ³rias para cada partÃ­cula
                const size = Math.random() * 8 + 4;
                const x = Math.random() * 100;
                const duration = Math.random() * 10 + 15;
                const delay = Math.random() * 5;
                
                // Cores aleatÃ³rias baseadas no tema
                const colors = ['#6a00ff', '#f72585', '#00ffaa', '#ff00aa'];
                const color = colors[Math.floor(Math.random() * colors.length)];
                
                particle.style.cssText = `
            left: ${x}%;
            width: ${size}px;
            height: ${size}px;
            background: ${color};
            --duration: ${duration}s;
            animation-delay: ${delay}s;
        `;
                
                particlesContainer.appendChild(particle);
        }
}

// FunÃ§Ã£o para redirecionar para os laboratÃ³rios
function redirectToLab(labType) {
        // AnimaÃ§Ã£o de clique
        const clickedCard = event.currentTarget;
        clickedCard.style.transform = 'scale(0.95)';
        
        setTimeout(() => {
                clickedCard.style.transform = '';
                
                // Redirecionar baseado no tipo de laboratÃ³rio
                switch (labType) {
                        case 'physics':
                                window.location.href = '/lab/labfis.html';
                                break;
                        case 'chemistry':
                                window.location.href = '/lab/labquim.html';
                                break;
                        case 'biology':
                                window.location.href = '/lab/lab-bio.html';
                                break;
                        case 'astronomy':
                                window.location.href = '/lab/lab-astro.html';
                                break;
                        default:
                                console.error('LaboratÃ³rio nÃ£o encontrado:', labType);
                }
        }, 150);
}

// AnimaÃ§Ãµes de hover nos cards
document.querySelectorAll('.lab-card').forEach(card => {
        card.addEventListener('mouseenter', () => {
                const icon = card.querySelector('.lab-icon');
                icon.style.transform = 'scale(1.2) rotate(10deg)';
        });
        
        card.addEventListener('mouseleave', () => {
                const icon = card.querySelector('.lab-icon');
                icon.style.transform = 'scale(1) rotate(0deg)';
        });
});

// AnimaÃ§Ãµes para os experimentos populares
document.querySelectorAll('.experiment-card').forEach(card => {
        card.addEventListener('mouseenter', () => {
                const icon = card.querySelector('.experiment-icon');
                icon.style.transform = 'scale(1.3) rotateY(360deg)';
                icon.style.transition = 'transform 0.6s ease';
        });
        
        card.addEventListener('mouseleave', () => {
                const icon = card.querySelector('.experiment-icon');
                icon.style.transform = 'scale(1) rotateY(0deg)';
        });
});

// Sistema de notificaÃ§Ãµes para laboratÃ³rios
function showLabNotification(message, type = 'info') {
        const notification = document.createElement('div');
        notification.style.cssText = `
        position: fixed;
        top: 20px;
        right: 20px;
        background: var(--fundo-card);
        color: var(--texto);
        padding: 15px 20px;
        border-radius: 10px;
        border-left: 4px solid var(--destaque);
        box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
        z-index: 1000;
        max-width: 300px;
        transform: translateX(100%);
        transition: transform 0.3s ease;
    `;
        
        notification.innerHTML = `
        <div style="display: flex; align-items: center; gap: 10px;">
            <i class="fas fa-flask" style="color: var(--destaque);"></i>
            <span>${message}</span>
        </div>
    `;
        
        document.body.appendChild(notification);
        
        // Animar entrada
        setTimeout(() => {
                notification.style.transform = 'translateX(0)';
        }, 100);
        
        // Remover apÃ³s 3 segundos
        setTimeout(() => {
                notification.style.transform = 'translateX(100%)';
                setTimeout(() => {
                        document.body.removeChild(notification);
                }, 300);
        }, 3000);
}

// Simular atividade dos laboratÃ³rios
setInterval(() => {
        const activities = [
                'ðŸ§ª Novo experimento iniciado no Lab de QuÃ­mica',
                'âš¡ SimulaÃ§Ã£o de campo magnÃ©tico ativa na FÃ­sica',
                'ðŸ”¬ AnÃ¡lise microscÃ³pica em andamento na Biologia',
                'ðŸŒŒ ObservaÃ§Ã£o de nebulosa detectada na Astronomia'
        ];
        
        const randomActivity = activities[Math.floor(Math.random() * activities.length)];
        
        // Chance de 30% de mostrar notificaÃ§Ã£o
        if (Math.random() < 0.3) {
                showLabNotification(randomActivity);
        }
}, 10000); // A cada 10 segundos

// EstatÃ­sticas dos laboratÃ³rios (simuladas)
const labStats = {
        physics: {
                experiments: 156,
                activeUsers: 234,
                completionRate: 87
        },
        chemistry: {
                experiments: 198,
                activeUsers: 189,
                completionRate: 92
        },
        biology: {
                experiments: 143,
                activeUsers: 267,
                completionRate: 89
        },
        astronomy: {
                experiments: 89,
                activeUsers: 145,
                completionRate: 94
        }
};

// FunÃ§Ã£o para mostrar estatÃ­sticas (pode ser chamada para debug)
window.showLabStats = function() {
        console.table(labStats);
};

// Sistema de conquistas (placeholder)
const achievements = [
        { id: 1, name: 'Primeiro Experimento', description: 'Complete seu primeiro experimento', unlocked: false },
        { id: 2, name: 'Cientista Dedicado', description: 'Complete 10 experimentos', unlocked: false },
        { id: 3, name: 'Mestre dos LaboratÃ³rios', description: 'Visite todos os 4 laboratÃ³rios', unlocked: false },
        { id: 4, name: 'Descobridor', description: 'Encontre um resultado inesperado', unlocked: false }
];

// FunÃ§Ã£o para desbloquear conquista
function unlockAchievement(achievementId) {
        const achievement = achievements.find(a => a.id === achievementId);
        if (achievement && !achievement.unlocked) {
                achievement.unlocked = true;
                showLabNotification(`ðŸ† Conquista desbloqueada: ${achievement.name}`, 'achievement');
        }
}

// Salvar progresso no localStorage
function saveProgress() {
        localStorage.setItem('labmania_progress', JSON.stringify({
                achievements: achievements,
                lastVisit: new Date().toISOString()
        }));
}

// Carregar progresso do localStorage
function loadProgress() {
        const saved = localStorage.getItem('labmania_progress');
        if (saved) {
                const progress = JSON.parse(saved);
                // Aplicar progresso carregado
                console.log('Progresso carregado:', progress);
        }
}

// Inicializar progresso
loadProgress();

// Salvar progresso quando a pÃ¡gina for fechada
window.addEventListener('beforeunload', saveProgress);

